mapper.update:
/* updateCarLoad */
UPDATE TB_CARI_LOAD SET
      DEL_YN = 'N'
    , LAST_CHPR_ID = #{lastChprId}
    , LAST_CHG_DTTM = SYSTIMESTAMP
    , APRE_YN_CD    = #{apreYnCd}
    , AUDT_OPIN_CN =  #{audtOpinCn}
WHERE DEL_YN = 'N'
AND LOAD_RQST_NO = #{loadRqstNo}

/* updateCarLoadExt */
UPDATE ALPASSEXT.TB_CARE_LOAD SET
      DEL_YN = 'N'
    , LAST_CHPR_ID = #{lastChprId}
    , LAST_CHG_DTTM = SYSTIMESTAMP
    , APRE_YN_CD    = #{apreYnCd}
    , AUDT_OPIN_CN =  #{audtOpinCn}
    , PRCS_DTTM =  SYSTIMESTAMP
    , PRCS_STTS_CD =  #{prcsSttsCd}
WHERE DEL_YN = 'N'
AND LOAD_RQST_MGMT_NO = #{loadRqstMgmtNo}

/* deleteCarLoadBlCntrInt */
DELETE FROM TB_CARI_LOAD_BL_CNTR
WHERE DEL_YN = 'N'
AND LOAD_RQST_NO = #{loadRqstNo}
AND BL_SRNO = #{blSrno}

/* deleteCarLoadBlCntrExt */
DELETE FROM ALPASSEXT.TB_CARE_LOAD_BL_CNTR
WHERE DEL_YN = 'N'
AND LOAD_RQST_MGMT_NO = #{loadRqstMgmtNo}
AND BL_SRNO = #{blSrno}

/* deleteCarLoadBlExfmInt */
DELETE FROM TB_CARI_LOAD_BL_EXFM
