mapper.select:
/* selectCarLoadHblSrno */
SELECT *  FROM TB_CARI_LOAD_BL WHERE LOAD_RQST_NO = #{loadRqstNo} AND BL_PT_CD = 'H' AND BL_NO IN
    (SELECT BL_NO  FROM TB_CARI_LOAD_BL WHERE LOAD_RQST_NO = #{loadRqstNo} AND BL_SRNO = #{blSrno})

