mapper.select:
/* CarLoadRprt.selectCarLoadRprtItem */


     AND  T1.LDUN_RPRT_NO = #{ldunRprtNo}  /** N° De Rapport Sur Le Déchargement !== 하역보고번호 ==! */


     AND  T1.RPRT_DGCNT = #{rprtDgcnt}  /** N° ordre de rapport !== 보고차수 ==! */

/* CarLoadRprt.selectCarLoadRprtList */
AND T1.CAG_RQST_TP_CD = #{ cagRqstTpCd }
AND T3.audt_emp_id = #{ pbsrNo } /** ID de l'inspecteur !==검사직원ID==! */
AND T3.audt_emp_id = #{ audtEmpId } /** ID de l'inspecteur !==검사직원ID==! */
/** Date et heure de traitement !==처리일시==! */ AND T3.prcs_dttm >= TO_DATE(#{prcsDttmFrom},'DD/MM/YYYY')
/** Date et heure de traitement !==처리일시==! */ AND T3.prcs_dttm < TO_DATE(#{prcsDttmTo},'DD/MM/YYYY')+1
/** Date effective de déchargement !==실제하역일자==! */ AND T1.REAL_LDUN_END_DT >= TO_DATE(#{realLdunDtFrom},'DD/MM/YYYY') AND T1.REAL_LDUN_END_DT < TO_DATE(#{realLdunDtTo},'DD/MM/YYYY')+1
AND UPPER(T2.SLNG_RGSR_NO) like UPPER(#{slngRgsrNo}) || '%' /** N° d'enregistrement de voyage !==운항등록번호==! */
AND UPPER(CC.carr_nm) = UPPER(#{carrNm}) || '%'
AND UPPER(T2.trnp_meth_nm) like UPPER(#{trnpMethNm}) || '%' /** Nom de moyen de transport !==운송수단명==! */
AND UPPER(T2.trnp_meth_idfy_no) like UPPER(#{trnpMethIdfyNo}) || '%' /** N° d'identification du moyen de transport !==운송수단식별번호==! */
AND UPPER(T2.trnp_rfer_no) like UPPER(#{trnpRferNo}) || '%' /** N° de voyage/N° de vol/N° d'itinéraire !==운송참고번호==! */
AND T3.prcs_stts_cd= #{ prcsSttsCd } /** Code de statut de traitement !==처리상태코드==! */
AND T1.JRSD_ORGN_CD IN (
SELECT unnest(string_to_array(alpassint.fn_cari_get_orgcd('O', #{jrsdOrgnCd}), ','))
SELECT unnest(string_to_array(alpassint.fn_cari_get_orgcd('P', #{frstRegstId}), ','))
) ORDER BY t1.trsn_dttm DESC

/* CarLoadRprt.selectCariLoadRprtBlTotCnt */ SELECT COUNT(*) AS TOT_CNT FROM TB_CARI_LDUN_RPRT_BL T1 WHERE 1=1 AND T1.LDUN_RPRT_NO = #{ldunRprtNo} /** N° de séquence de rapport sur le déchargement !==하역보고일련번호==! */
AND T1.DSCR_TP_CD = 'OK' /** Déficit/Excédent != 불일치구분코드 =! */
AND T1.DSCR_TP_CD != 'OK' /** Déficit/Excédent != 불일치구분코드 =! */
AND T1.RPRT_DGCNT = #{rprtDgcnt} /** N° ordre de rapport !== 보고차수 ==! */
AND T1.BL_NO = #{blNo}

/* CarLoadRprt.selectCariLoadRprtCntrTotCnt*/ SELECT COUNT(*) AS TOT_CNT FROM TB_CARI_LDUN_RPRT_CNTR T1 WHERE 1=1 AND T1.LDUN_RPRT_NO = #{ldunRprtNo} /** N° de séquence de rapport sur le déchargement !==하역보고일련번호==! */
AND T1.DSCR_TP_CD = 'OK' /** Déficit/Excédent != 불일치구분코드 =! */
AND T1.DSCR_TP_CD != 'OK' /** Déficit/Excédent != 불일치구분코드 =! */
AND T1.RPRT_DGCNT = #{rprtDgcnt} /** N° ordre de rapport !== 보고차수 ==! */
AND T1.BL_NO = #{blNo}
AND T1.CNTR_NO = #{cntrNo}

/* CarLoadRprt.selectCariLoadRprtCntrList*/
SELECT #{totCnt} AS TOT_CNT , SQLRSLT.* , ROWNUM as RNUM FROM (
SELECT T1.LDUN_RPRT_NO /** N° de rapport sur le déchargement !==하역보고번호==! */ , T1.BL_NO /** N° de BL !==BL번호==! */ , T1.CNTR_SRNO /** N° de séquence du conteneur !==컨테이너일련번호==! */ , T1.RPRT_DGCNT /** Nombre de rapport !==보고차수==! */ , T1.DSCR_TP_CD /** DéficitExcédent !==불일치구분코드==! */ , FN_GET_CD_VDVAL_NM('CAR_0032', T1.DSCR_TP_CD, #{langCd}) AS DSCR_TP_NM /** DéficitExcédent !==불일치구분코드==! */ , T1.CNTR_NO /** N° de conteneur !==컨테이너번호==! */ , T1.CNTR_TP_CD /** Code de type de conteneur !==컨테이너구분코드==! */ , FN_GET_CD_VDVAL_NM('CAR_0020', T1.CNTR_TP_CD, #{langCd}) AS CNTR_TP_NM /** Code de type de conteneur !==컨테이너구분코드==! */ , T1.PCKG_GCNT /** Nombre de colis !==포장개수==! */ , T1.PCKG_UT_CD /** Code de l'unité de colis !==포장단위코드==! */ , FN_GET_CD_VDVAL_NM('CAR_0021', T1.PCKG_UT_CD, #{langCd}) AS PCKG_UT_NM /** Code de l'unité de colis !==포장단위코드==! */ , T1.TTWG /** Poids brut !==총중량==! */ , T1.SEAL_NO1 /** N°1 de scellé !==봉인번호1==! */ , T1.SEAL_NO2 /** N°2 de scellé !==봉인번호2==! */ , T1.SEAL_NO3 /** N°3 de scellé !==봉인번호3==! */ , T1.IBOBZ_CD AS IBOBZ_CD /** Code de zone sous douane !== 보세구역코드 ==! */ , (SELECT IB.IBOBZ_NM FROM TB_CARI_IBOBZ IB WHERE IB.IBOBZ_CD = T1.IBOBZ_CD) AS IBOBZ_NM /** Nom d'entrepôt !== 보세창고명==! */ , T1.INSC_CFRM_DT /** Date de vérification de l'inspection !==검사확인일자==! */ , T1.APRE_YN_CD /** Autorisation O/N !==승인여부코드==! */ , T1.APRE_YN_CD AS OLD_APRE_YN_CD /** Autorisation O/N !==승인여부코드==! */ , FN_GET_CD_VDVAL_NM('CAG_0023', T1.APRE_YN_CD, #{langCd}) AS APRE_YN_NM /** Autorisation O/N !==승인여부코드==! */ , T1.RTRN_RSN_CN /** Motif de rejet !==반려사유내용==! */ , T1.DEL_YN /** Suppression ON !==삭제여부==! */ , T1.FRST_REGST_ID /** ID du premier enregistrant !==최초등록자ID==! */ , T1.LAST_CHPR_ID /** ID du modificateur final !==최종변경자ID==! */ , T2.BL_PT_CD /** Code De Type De Bl !== BL유형코드 ==! */ FROM TB_CARI_LDUN_RPRT_CNTR T1 LEFT JOIN (SELECT TCLR.LDUN_RPRT_NO , TCLB.BL_NO , TCLB.BL_PT_CD , TCLBC.CNTR_SRNO , TCLBC.CNTR_NO FROM TB_CARI_LOAD TCL , TB_CARI_LOAD_BL TCLB , TB_CARI_LOAD_BL_CNTR TCLBC , TB_CARI_LDUN_RPRT TCLR WHERE 1=1 AND TCL.DEL_YN = 'N' AND TCLB.DEL_YN = 'N' AND TCLR.DEL_YN = 'N' AND TCLB.BL_PT_CD = 'M' --AND ((TCLB.BL_PT_CD = 'M' AND TCLB.BL_TP_CD = 'S') OR TCLB.BL_PT_CD = 'H') AND TCL.LOAD_RQST_NO = TCLB.LOAD_RQST_NO AND TCL.LOAD_RQST_NO = TCLBC.LOAD_RQST_NO AND TCL.LOAD_RQST_NO = TCLR.MRN AND TCLB.BL_SRNO = TCLBC.BL_SRNO ) T2 ON T2.LDUN_RPRT_NO = T1.LDUN_RPRT_NO AND T2.BL_NO = T1.BL_NO AND T2.CNTR_SRNO = T1.CNTR_SRNO AND T2.CNTR_NO = T1.CNTR_NO WHERE 1=1 AND T1.LDUN_RPRT_NO = #{ldunRprtNo} /** N° de séquence de rapport sur le déchargement !==하역보고일련번호==! */
AND T1.DSCR_TP_CD = 'OK' /** Déficit/Excédent != 불일치구분코드 =! */
AND T1.DSCR_TP_CD != 'OK' /** Déficit/Excédent != 불일치구분코드 =! */
AND T1.RPRT_DGCNT = #{rprtDgcnt} /** N° ordre de rapport !== 보고차수 ==! */
AND T1.BL_NO = #{blNo}
AND T1.CNTR_NO = #{cntrNo}
ORDER BY T1.CNTR_NO

mapper.update:
/* apreYnCdCarLoadRprtBl */ UPDATE TB_CARI_LDUN_RPRT_BL SET insc_cfrm_dt = TO_CHAR(TO_DATE( #{inscCfrmDt}, 'YYYY/MM/DD'),'YYYYMMDD') /** Date de vérification de la visite !== 검사확인일자 ==! */ , apre_yn_cd = #{apreYnCd} /** Autorisation O/N !== 승인여부코드 ==! */ , rtrn_rsn_cn = #{rtrnRsnCn} /** Motif De Rejet !== 반려사유내용 ==! */ WHERE 1=1 AND ldun_rprt_no = #{ldunRprtNo}
AND bl_no = #{blNo} /** N° De Bl !== BL번호 ==! */
AND dscr_tp_cd = 'OK' /** Déficit/Excédent != 불일치구분코드 =! */
AND dscr_tp_cd != 'OK' /** Déficit/Excédent != 불일치구분코드 =! */

/* apreYnCdCarLoadRprtCntr */ UPDATE TB_CARI_LDUN_RPRT_CNTR SET insc_cfrm_dt = TO_CHAR(TO_DATE( #{inscCfrmDt}, 'YYYY/MM/DD'),'YYYYMMDD') /** Date de vérification de la visite !== 검사확인일자 ==! */ , apre_yn_cd = #{apreYnCd} /** Autorisation O/N !== 승인여부코드 ==! */ , rtrn_rsn_cn = #{rtrnRsnCn} /** Motif De Rejet !== 반려사유내용 ==! */ WHERE 1=1 AND ldun_rprt_no = #{ldunRprtNo}
AND bl_no = #{blNo} /** N° De Bl !== BL번호 ==! */ AND cntr_no = #{cntrNo} /** N° De Conteneur !== 컨테이너번호 ==! */
AND dscr_tp_cd = 'OK' /** Déficit/Excédent != 불일치구분코드 =! */
AND dscr_tp_cd != 'OK' /** Déficit/Excédent != 불일치구분코드 =! */

mapper.select:
        /* checkApreYnCdLoadRprt */
         SELECT ( T1.CNT + T2.CNT ) CNT
											FROM   (SELECT COUNT(*) CNT
											        FROM   TB_CARI_LDUN_RPRT_BL
											        WHERE  LDUN_RPRT_NO = #{ldunRprtNo}   

											               AND (APRE_YN_CD IS NULL OR APRE_YN_CD = '')


											               AND APRE_YN_CD = 'N'

											               ) T1,
											       (SELECT COUNT(*) CNT
											        FROM   TB_CARI_LDUN_RPRT_CNTR
											        WHERE  LDUN_RPRT_NO = #{ldunRprtNo}   

											               AND (APRE_YN_CD IS NULL OR APRE_YN_CD = '')


											               AND APRE_YN_CD = 'N'

											               ) T2

        /* selectCheckApreYnCdLoadRprtBl */
        SELECT BL_NO
          FROM TB_CARI_LDUN_RPRT_BL
         WHERE LDUN_RPRT_NO = #{ldunRprtNo}

								           AND (APRE_YN_CD IS NULL OR APRE_YN_CD = '')


								           AND APRE_YN_CD = 'N'

        /* selectCheckApreYnCdLoadRprtCntr */
        SELECT BL_NO, CNTR_NO
          FROM TB_CARI_LDUN_RPRT_CNTR
         WHERE LDUN_RPRT_NO = #{ldunRprtNo}

							           AND (APRE_YN_CD IS NULL OR APRE_YN_CD = '')


							           AND APRE_YN_CD = 'N'
