/* alpass.ipt.car.trnpcvy.cvy.mapper.CarTrnpMdfyMapper.deleteCareRlbrPrng */
UPDATE TB_CARE_RLBR_PRNG
SET    DEL_YN = 'Y'
  , LAST_CHPR_ID    = #{lastChprId}    /**  Id Du Modificateur Final  !== 최종변경자ID ==!  */
  , LAST_CHG_DTTM   = SYSTIMESTAMP     /**  Date Et Heure De Modification Finale  !== 최종변경일시 ==!  */
WHERE  RLBR_TP_CD   = #{rlbrTpCd}
  AND    RLBR_PT_CD   = #{rlbrPtCd}
  AND    RELA_NO      = #{relaNo} /**  N° de demande de transfert  !== 운송신청번호 ==!  */
  AND    CAG_MGMT_NO  = #{cagMgmtNo}
  AND    IBOBZ_CD     = #{ibobzCd}
