/** selectTaxCd On consulte le code de taxe. !== 세금 코드 조회 ==! */
SELECT A.TAX_CD /** Code De Taxe !== 세금코드 ==! */ , NVL((SELECT TAX_CLCU_BASE_CN FROM TB_CLRI_TAX_CLCU_BASE WHERE TAX_CD = A.TAX_CD AND USE_YN = 'Y' AND TO_CHAR(SYSDATE, 'YYYYMMDD') BETWEEN APLY_STRT_DT AND APLY_END_DT),(SELECT TAX_CLCU_BASE_CN FROM TB_CLRI_TAX_CLCU_BASE WHERE TAX_CD = A.TAX_CD AND USE_YN = 'Y' ORDER BY BASE_SRNO DESC LIMIT 1)) TAX_CLCU_BASE_CN , A.TAX_CD_NM /** Nom Du Code De Taxe !== 세금코드명 ==! */ , A.TAX_CTGY_CD /** Code De Catégorie De Taxe !== 세금카테고리코드 ==! */ , (SELECT CD_VDVAL_NM FROM TB_COM_COMN_CD_D_LANG WHERE COMN_CD = 'CLR_0058' AND LANG_CD = #{langCd} AND CD_VDVAL = A.TAX_CTGY_CD) TAX_CTGY_NM , A.TRIF_RT_TP_CD /** Code de type de tarif !== 관세율구분코드 ==! */ , (SELECT CD_VDVAL_NM FROM TB_COM_COMN_CD_D_LANG WHERE COMN_CD = 'CLR_0041' AND LANG_CD = #{langCd} AND CD_VDVAL = A.TRIF_RT_TP_CD) TRIF_RT_TP_NM , A.BDGT_CD /** Code De Budget !== 예산코드 ==! */ , A.BDGT_DTL_NO /** N° De Ligne Budgétaire !== 예산상세번호 ==! */ , A.APLY_PROR_RNK /** Priorité de l'application !== 적용우선순위 ==! */ , A.USE_YN /** Utilisé ON !== 사용여부 ==! */ , NVL((SELECT LSPR_TXBS_CN FROM TB_CLRI_TAX_CLCU_BASE C WHERE C.TAX_CD = A.TAX_CD AND TO_DATE(TO_CHAR(SYSTIMESTAMP,'YYYYMMDD'),'YYYYMMDD') BETWEEN TO_DATE(APLY_STRT_DT,'YYYYMMDD') AND TO_DATE(APLY_END_DT,'YYYYMMDD') ORDER BY BASE_SRNO DESC LIMIT 1),(SELECT LSPR_TXBS_CN FROM TB_CLRI_TAX_CLCU_BASE C WHERE C.TAX_CD = A.TAX_CD ORDER BY BASE_SRNO DESC LIMIT 1)) LSPR_TXBS_CN , NVL((SELECT CRLN_TXBS_CN FROM TB_CLRI_TAX_CLCU_BASE C WHERE C.TAX_CD = A.TAX_CD AND TO_DATE(TO_CHAR(SYSTIMESTAMP,'YYYYMMDD'),'YYYYMMDD') BETWEEN TO_DATE(APLY_STRT_DT,'YYYYMMDD') AND TO_DATE(APLY_END_DT,'YYYYMMDD') ORDER BY BASE_SRNO DESC LIMIT 1),(SELECT CRLN_TXBS_CN FROM TB_CLRI_TAX_CLCU_BASE C WHERE C.TAX_CD = A.TAX_CD ORDER BY BASE_SRNO DESC LIMIT 1)) CRLN_TXBS_CN FROM TB_CLRI_TAX_CD A WHERE USE_YN = 'Y' AND DEL_YN = 'N'
AND REPLACE(TAX_CD,'.','') = REPLACE(UPPER(#{taxCd}),'.','')
AND TAX_CD_NM LIKE #{taxCdNm} || '%'
AND TAX_CTGY_CD = #{taxCtgyCd}
ORDER BY TAX_CD

/** selectApcCd APC코드 조회*/
SELECT A.APC_CD , A.APC_NM , A.PSMGT_YN , A.PSMGT_PRID FROM TB_CLRI_APC_CD A WHERE A.DEL_YN = 'N'
AND A.PSMGT_YN = #{psmgtYn}
AND A.PSMGT_PRID = #{psmgtPrid}
AND A.APC_CD = #{apcCd}
AND A.APC_NM LIKE #{apcNm} || '%'
ORDER BY A.APC_CD

/** selectApcExcpMtrCdList APC예외사항코드 조회*/
SELECT A.APC_CD , B.APC_NM , A.APC_EXCP_MTR_CD , A.APC_EXCP_MTR_CD_NM , TO_CHAR(TO_DATE(A.APLY_STRT_DT, 'YYYYMMDD'), 'DD/MM/YYYY') AS APLY_STRT_DT , TO_CHAR(TO_DATE(A.APLY_END_DT, 'YYYYMMDD'), 'DD/MM/YYYY') AS APLY_END_DT FROM TB_CLRI_APC_EXCP_MTR_CD_MGMT A, TB_CLRI_APC_CD B WHERE A.DEL_YN = 'N' AND B.DEL_YN = 'N' AND A.APC_CD = B.APC_CD
AND A.APC_EXCP_MTR_CD = #{apcExcpMtrCd}
AND APLY_STRT_DT <= TO_CHAR(TO_DATE(#{aplyDtFrom}, 'DD/MM/YYYY'), 'YYYYMMDD') AND TO_CHAR(TO_DATE(#{aplyDtTo}, 'DD/MM/YYYY'), 'YYYYMMDD') <= APLY_END_DT
AND A.APC_CD = #{apcCd}
AND B.APC_NM LIKE #{apcNm} || '%'
ORDER BY A.APC_CD, A.APC_EXCP_MTR_CD

/** selectApcCd APC코드 조회*/
SELECT A.APC_CD , B.EPC_CD , A.APC_NM , A.PSMGT_YN , A.PSMGT_PRID , NVL(B.REGM_PERM_NEED_YN, 'N') AS REGM_PERM_NEED_YN , B.REGM_MDL_CD , NVL(B.ffmn_trgt_yn, 'N') AS ffmn_trgt_yn FROM TB_CLRI_APC_CD A, TB_CLRI_EPC_APC_CD_MPNG_BRKD B WHERE A.DEL_YN = 'N' AND B.APC_CD(+) = A.APC_CD AND B.DEL_YN(+) = 'N'
AND A.PSMGT_YN = #{psmgtYn}
AND A.PSMGT_PRID = #{psmgtPrid}
AND B.EPC_CD(+) = #{epcCd} AND TO_CHAR(CURRENT_DATE, 'YYYYMMDD') BETWEEN B.MPNG_APLY_STRT_DT AND B.MPNG_APLY_END_DT /** 신청일자 (입력조건) */
AND B.REGM_PERM_NEED_YN(+) = #{regmPermNeedYn}
AND A.APC_CD = #{apcCd}
AND A.APC_NM LIKE #{apcNm} || '%'
AND B.REGM_MDL_CD = #{regmMdlCd}
ORDER BY A.APC_CD

