/** selectEpcCd : EPC코드 조회*/
SELECT A.EPC_CD /** Code EPC !== EPC코드 ==! */ , A.EPC_NM /** Nom de l'EPC !== EPC명 ==! */ , A.DCLR_PT_CD /** Code de type de déclaration !== 신고유형코드 ==! */ , A.CAG_CMVL_YN /** Contrôle Manifeste On !== 화물대사여부 ==! */ , A.EXFM_PRCS_YN /** Apurement D'Exportation On !== 수출이행처리여부 ==! */ , A.TACP_RLSE_PRCS_TP_CD /** Code De Division Entre Le Traitement Au Niveau De L'Autorisation D'Enlèvement Et Le Traitement !== 수리시반출처리구분코드(CLR_0072) ==! */ , CASE A.DCLR_PT_CD WHEN 'IM5' THEN (SELECT TO_NUMBER(prmt_val_cn) FROM tb_com_prmt WHERE prmt_id = 'CLR_0002') WHEN 'IM7' THEN (SELECT TO_NUMBER(prmt_val_cn) FROM tb_com_prmt WHERE prmt_id = 'CLR_0004') WHEN 'END' THEN (SELECT TO_NUMBER(prmt_val_cn) FROM tb_com_prmt WHERE prmt_id = 'CLR_0005') WHEN 'EX2' THEN (SELECT TO_NUMBER(prmt_val_cn) FROM tb_com_prmt WHERE prmt_id = 'CLR_0003') WHEN 'ZFI' THEN (SELECT TO_NUMBER(prmt_val_cn) FROM tb_com_prmt WHERE prmt_id = 'CLR_0006') WHEN 'ZFE' THEN (SELECT TO_NUMBER(prmt_val_cn) FROM tb_com_prmt WHERE prmt_id = 'CLR_0006') ELSE CASE A.EPC_CD WHEN 'EPCXXX' THEN (SELECT TO_NUMBER(prmt_val_cn) FROM tb_com_prmt WHERE prmt_id = 'CLR_0011') ELSE 0 END END AS PSMGT_FFMN_XPIR_PRID , TO_CHAR(TO_DATE(EPC_APLY_STRT_DT, 'YYYYMMDD'), 'DD/MM/YYYY') EPC_APLY_STRT_DT , TO_CHAR(TO_DATE(EPC_APLY_END_DT, 'YYYYMMDD'), 'DD/MM/YYYY') EPC_APLY_END_DT FROM TB_CLRI_EPC_CD A WHERE A.DEL_YN = 'N'
AND substr(A.epc_cd, 2, 1) <> '1'
AND TO_CHAR(CURRENT_DATE,'YYYYMMDD') BETWEEN EPC_APLY_STRT_DT AND EPC_APLY_END_DT
AND A.DCLR_PT_CD = #{dclrPtCd}
AND A.EPC_CD LIKE #{epcCd} || '%'
AND A.EPC_NM LIKE #{epcNm} || '%'
AND A.DCLR_PT_CD IN
#{item}
ORDER BY A.SORT_ORDR

