/** ClreCom_selectBusiComHsCdLangList */
SELECT HS_CD , TO_CHAR(TO_DATE(HS_APLY_STRT_DT, 'YYYYMMDD'), 'DD/MM/YYYY') AS HS_APLY_STRT_DT , TO_CHAR(TO_DATE(HS_APLY_END_DT, 'YYYYMMDD'), 'DD/MM/YYYY') AS HS_APLY_END_DT , HS_CLSF_CD , UPR_HS_CD , TO_CHAR(TO_DATE(UPR_HS_APLY_STRT_DT, 'YYYYMMDD'), 'DD/MM/YYYY') AS UPR_HS_APLY_STRT_DT , HS_DESC , qty_ut_cd QTY_UT1_CD , BRND_NM_WRTE_YN , INGR_WRTE_YN , LAST_YN , MRKT_PRC_CD , DPRC_RT , hs_err_pvnt_val , adit_tx_qty_ut_cd
FROM TB_CLRI_HS_CD WHERE 1 = 1
AND HS_CD LIKE #{hsCd}||'%'
AND HS_APLY_STRT_DT <= TO_CHAR(TO_DATE(#{hsAplyStrtDt}, 'DD/MM/YYYY'), 'YYYYMMDD')
AND HS_APLY_END_DT >= TO_CHAR(TO_DATE(#{hsAplyEndDt}, 'DD/MM/YYYY'), 'YYYYMMDD')
AND HS_CLSF_CD = #{hsClsfCd}
AND HS_DESC LIKE '%'||#{hsDesc}||'%'
AND LAST_YN = #{lastYn}
ORDER BY HS_CD, HS_APLY_STRT_DT

/* selectComEmpList */
SELECT CSTM_CD , (SELECT A.CSTM_CD||' | '||ORGN_NM FROM TB_POTI_ORGN WHERE ORGN_CD = A.CSTM_CD AND ROWNUM=1) AS CSTM_NM , DEPT_CD , (SELECT A.DEPT_CD||' | '||ORGN_NM FROM TB_POTI_ORGN WHERE ORGN_CD = A.DEPT_CD AND ROWNUM=1) AS DEPT_NM , A.EMP_NM , A.PBSR_NO , A.CLPS_NM FROM ( SELECT (SELECT ORGN_CD FROM TB_POTI_ORGN WHERE DEL_YN = 'N' AND ORGN_TP_CD = 'INS' /**지역관할세관*/ AND ROWNUM = 1 START WITH ORGN_MGMT_CD = TPE.ORGN_MGMT_CD CONNECT BY PRIOR UPR_ORGN_MGMT_CD = ORGN_MGMT_CD) AS CSTM_CD , (SELECT ORGN_CD FROM TB_POTI_ORGN WHERE DEL_YN = 'N' AND ORGN_TP_CD IN('BRI') /**브리가드*/ AND ROWNUM = 1 START WITH ORGN_MGMT_CD = TPE.ORGN_MGMT_CD CONNECT BY PRIOR UPR_ORGN_MGMT_CD = ORGN_MGMT_CD) AS DEPT_CD , VPRU.PBSR_NO AS INSC_EMP_ID , VPRU.PBSR_NO , TPE.EMP_NM , FN_GET_CD_VDVAL_NM('COM_0064', TPE.CLPS_CD, 'KO') AS CLPS_NM FROM VW_POTI_ROLE_UNFC VPRU , TB_POTI_EMP TPE WHERE TPE.DEL_YN = 'N' AND VPRU.PBSR_NO = TPE.PBSR_NO AND FN_GET_EMP_BWRK_STTS(TPE.PBSR_NO,'Y') = 'TRUE' /** 부재중건이 없으면 근무중 상태로 봄 */
AND VPRU.ROLE_ID = #{roleId} /**현장검사원 권한*/
) A WHERE 1 = 1
AND A.CSTM_CD = #{cstmCd}
AND A.DEPT_CD = #{deptCd}
AND A.PBSR_NO = #{pbsrNo}
AND UPPER(A.EMP_NM) LIKE '%'||UPPER(#{empNm})||'%'
ORDER BY A.PBSR_NO

/* alpass.batch.ipt.clr.com.mapper.ClrComServiceMapper.selectHldyYn */
SELECT COUNT(*)
FROM TB_COM_HLDY
WHERE DEL_YN = 'N'
  AND BASE_DT = #{baseDt}

/* selectComAdorEmpList */
SELECT DISTINCT A.PBSR_NO , A.EMP_NM , ( SELECT ORGN_CD FROM TB_POTI_ORGN WHERE ORGN_MGMT_CD = A.SCTR_MGMT_CD ) AS SCTR_CD , C.ORGN_CD AS CSTM_CD , (SELECT C.ORGN_CD||' | '||ORGN_NM FROM TB_POTI_ORGN WHERE ORGN_CD = C.ORGN_CD AND ROWNUM=1) AS CSTM_NM , D.ORGN_CD AS DEPT_CD , (SELECT D.ORGN_CD||' | '||ORGN_NM FROM TB_POTI_ORGN WHERE ORGN_CD = D.ORGN_CD AND ROWNUM=1) AS DEPT_NM , C.ORGN_NM , FN_GET_CD_VDVAL_NM('COM_0064', A.CLPS_CD, 'KO') AS CLPS_NM FROM TB_POTI_EMP A , VW_POTI_ROLE_UNFC B , TB_POTI_ORGN C , TB_POTI_ORGN D WHERE A.DEL_YN = 'N' AND C.DEL_YN = 'N' AND D.DEL_YN = 'N' AND A.PBSR_NO = B.PBSR_NO
AND A.CSTM_MGMT_CD = C.ORGN_MGMT_CD AND A.DEPT_MGMT_CD = D.ORGN_MGMT_CD
AND FN_GET_EMP_BWRK_STTS(A.PBSR_NO,'N') = 'TRUE'
AND B.ROLE_ID = #{roleId}
AND C.ORGN_CD = #{cstmCd}
AND D.ORGN_CD = #{deptCd}
AND A.PBSR_NO = #{pbsrNo}
AND UPPER(A.EMP_NM) LIKE '%'||UPPER(#{empNm})||'%'
AND A.ORGN_MGMT_CD IN ( SELECT ORGN_MGMT_CD FROM TB_POTI_ORGN START WITH ORGN_CD = #{deptCd} CONNECT BY PRIOR ORGN_MGMT_CD = UPR_ORGN_MGMT_CD AND DEL_YN='N' AND ORGN_TP_CD = 'INP' )
ORDER BY A.PBSR_NO

