SELECT HS_EXCP_MTR_CD COMN_CD , HS_EXCP_MTR_NM AS COMN_CD_NM FROM TB_CLRI_HS_EXCP_MTR_CD_MGMT A, TB_CLRI_HS_CD B WHERE A.DEL_YN = 'N' AND B.DEL_YN = 'N' AND A.USE_YN = 'Y'
AND B.HS_CD = #{hsCd}
AND APLY_SCP_CD ='B' AND APLY_CD = #{apcCd}
AND A.HS_CD = B.HS_CD AND TO_CHAR(CURRENT_DATE, 'YYYYMMDD') BETWEEN B.HS_APLY_STRT_DT AND B.HS_APLY_END_DT

/** chkApcHsCdMltList APC HS코드 List 조회*/
SELECT COUNT(*) AS cnt
FROM tb_clri_apc_hs

WHERE from_hs10_cd <> to_hs10_cd

AND apc_cd = #{apcCd}

/** selectApcHsCdMltList APC HS코드 List 조회*/
SELECT b.hs_cd , b.hs_desc , ac.aply_strt_dt AS hs_aply_strt_dt , ac.aply_end_dt AS hs_aply_end_dt FROM tb_clri_apc_hs ac , tb_clri_hs_cd b WHERE b.del_yn = 'N' AND b.hs_clsf_cd = '10' AND LENGTH(b.hs_cd) = 10 AND TO_CHAR(SYSTIMESTAMP, 'YYYYMMDD') BETWEEN b.hs_aply_strt_dt AND b.hs_aply_end_dt AND (ac.aply_strt_dt >= b.hs_aply_strt_dt AND b.hs_aply_end_dt >= ac.aply_end_dt ) AND ac.del_yn = 'N' AND b.hs_cd = ac.from_hs10_cd AND ac.apc_cd = #{apcCd}
AND b.hs_cd = #{hsCd}
AND NOT EXISTS( SELECT 1 FROM TB_CLRI_APC_NAPLY_TAX_CD_MGMT c where ac.apc_cd = c.apc_cd AND ac.from_hs10_cd = c.hs_Cd and c.DEL_YN ='N' and TO_CHAR(SYSTIMESTAMP, 'YYYYMMDD') between c.aply_strt_dt and c.aply_end_dt and c.naply_tax_cd = #{taxCd} ) ORDER BY hs_cd, b.hs_aply_strt_dt, b.hs_aply_end_dt

/** selectApcHsCdMltAllList APC HS코드 List 조회*/
SELECT b.hs_cd , b.hs_desc , (SELECT aply_strt_dt FROM tb_clri_apc_hs ac where ac.apc_cd = #{apcCd} LIMIT 1 ) AS hs_aply_strt_dt , (SELECT aply_end_dt FROM tb_clri_apc_hs ac where ac.apc_cd = #{apcCd} LIMIT 1 ) AS hs_aply_end_dt FROM tb_clri_hs_cd b WHERE b.del_yn = 'N' AND LENGTH(b.hs_cd) = 10 AND TO_CHAR(SYSTIMESTAMP, 'YYYYMMDD') BETWEEN b.hs_aply_strt_dt AND b.hs_aply_end_dt
AND b.hs_cd = #{hsCd}
AND NOT EXISTS( SELECT 1 FROM TB_CLRI_APC_NAPLY_TAX_CD_MGMT c where ac.apc_cd = c.apc_cd and c.DEL_YN ='N' and TO_CHAR(SYSTIMESTAMP, 'YYYYMMDD') between c.aply_strt_dt and c.aply_end_dt and c.naply_tax_cd = #{taxCd} )

/** selectHsExcpMtrCdList */
SELECT HS_EXCP_MTR_CD AS ITEM,
	   HS_EXCP_MTR_NM AS ITEM_NM
FROM ALPASSINT.TB_CLRI_HS_EXCP_MTR_CD_MGMT
WHERE DEL_YN = 'N' AND HS_CD = #{hsCd}

		AND HS_EXCP_MTR_CD = #{hsExcpMtrCd}




			  AND  APLY_STRT_DT <= TO_CHAR(TO_DATE(#{aplyDtFrom}, 'DD/MM/YYYY'), 'YYYYMMDD') AND TO_CHAR(TO_DATE(#{aplyDtTo}, 'DD/MM/YYYY'), 'YYYYMMDD') <= APLY_END_DT 



		  AND TO_CHAR(SYSTIMESTAMP, 'YYYYMMDD') between aply_strt_dt and aply_end_dt



ORDER BY HS_CD,HS_EXCP_MTR_CD,APLY_STRT_DT

SELECT HS_CD , HS_DESC , TO_CHAR(TO_DATE(HS_APLY_STRT_DT, 'YYYYMMDD'), 'DD/MM/YYYY') AS HS_APLY_STRT_DT , HS_EXCP_MTR_CD , HS_EXCP_MTR_NM , APLY_SCP_CD , FN_GET_CD_VDVAL_NM('CLR_0071', APLY_SCP_CD, #{langCd}) AS APLY_SCP_NM , APLY_CD , SLCT_CLUS_SRNO , TO_CHAR(TO_DATE(APLY_STRT_DT, 'YYYYMMDD'), 'DD/MM/YYYY') AS APLY_STRT_DT , TO_CHAR(TO_DATE(APLY_END_DT, 'YYYYMMDD'), 'DD/MM/YYYY') AS APLY_END_DT , USE_YN FROM ( SELECT RANK() OVER(PARTITION BY A.HS_CD ORDER BY HS_APLY_STRT_DT DESC) AS RN , A.HS_CD , (SELECT HS_DESC FROM TB_CLRI_HS_CD B WHERE A.HS_CD = B.HS_CD AND A.HS_APLY_STRT_DT = B.HS_APLY_STRT_DT ) HS_DESC , A.HS_APLY_STRT_DT , A.HS_EXCP_MTR_CD , A.HS_EXCP_MTR_NM , A.APLY_SCP_CD , A.APLY_CD , A.SLCT_CLUS_SRNO , A.APLY_STRT_DT , A.APLY_END_DT , A.DEL_YN , A.USE_YN , A.FRST_REGST_ID , A.FRST_RGSR_DTTM , A.LAST_CHPR_ID , A.LAST_CHG_DTTM FROM TB_CLRI_HS_EXCP_MTR_CD_MGMT A WHERE 1=1 AND A.DEL_YN = 'N'
AND A.HS_CD LIKE '%'|| #{hsCd} || '%'
AND A.HS_EXCP_MTR_CD = #{hsExcpMtrCd}
AND NVL2(#{hsAplyStrtDt}, TO_CHAR(TO_DATE(#{hsAplyStrtDt}, 'DD/MM/YYYY'), 'YYYYMMDD'), TO_CHAR(SYSDATE,'YYYYMMDD')) = A.HS_APLY_STRT_DT
AND APLY_STRT_DT <= TO_CHAR(TO_DATE(#{aplyDtFrom}, 'DD/MM/YYYY'), 'YYYYMMDD') AND TO_CHAR(TO_DATE(#{aplyDtTo}, 'DD/MM/YYYY'), 'YYYYMMDD') <= APLY_END_DT
AND TO_CHAR(CURRENT_DATE,'YYYYMMDD') BETWEEN APLY_STRT_DT AND APLY_END_DT
AND A.APLY_SCP_CD = #{aplyScpCd}
)WHERE RN =1 ORDER BY HS_CD, APLY_STRT_DT

/* selectInputHsExcpMtrCdList */
