    HS_CD
  ,	HS_EXCP_MTR_CD
  , HS_EXCP_MTR_NM
FROM TB_CLRI_HS_EXCP_MTR_CD_MGMT A
WHERE A.DEL_YN = 'N'

	AND A.HS_CD = #{hsCd}


	AND A.HS_EXCP_MTR_CD = #{hsExcpMtrCd}


	AND A.APLY_SCP_CD = #{aplyScpCd}


	AND A.APLY_CD = #{aplyCd}


	AND  A.APLY_STRT_DT = TO_CHAR(TO_DATE(#{aplyStrtDt}, 'DD/MM/YYYY'), 'YYYYMMDD')


	AND  A.APLY_END_DT = TO_CHAR(TO_DATE(#{aplyEndDt}, 'DD/MM/YYYY'), 'YYYYMMDD')


	AND A.HS_EXCP_MTR_CD IN

		#{excpMtrCd}

/* ClrOtrqAdorHnotMgmtMapper.selectCntHldy !== 휴일여부 조회 ==! */
SELECT COUNT(*) + (CASE WHEN EXTRACT(dow FROM to_date(#{value},'yyyymmdd')) IN (5,6) THEN 1 ELSE 0 END) AS cnt
FROM TB_COM_HLDY
WHERE del_yn = 'N'
AND base_dt = #{value}
