mapper.update:
/* updateSadVlutAudtComnLastYn */
UPDATE tb_clri_ded_vlut_audt_comn
SET last_yn = 'N'
  , last_chpr_id = #{lastChprId}
  , last_chg_dttm = SYSTIMESTAMP
WHERE dtl_dcsh_no = #{dtlDcshNo}
  AND prc_ev_dgcnt = (SELECT MAX(prc_ev_dgcnt)
                      FROM tb_clri_ded_vlut_audt_comn
                      WHERE del_yn = 'N'
                        AND dtl_dcsh_no = #{dtlDcshNo})

/* updateSadSplmQryLastYn */
UPDATE tb_clri_ded_splm_qry
SET last_yn = 'N'
  , last_chpr_id = #{lastChprId}
  , last_chg_dttm = SYSTIMESTAMP
WHERE dtl_dcsh_no = #{dtlDcshNo}
  AND last_yn = 'Y'

/* updateSadCsclPstpRlse */
UPDATE TB_CLRI_DED_CSCL_PSTP
SET cscl_pstp_rele_dttm = SYSDATE
  , cscl_pstp_rele_cn = #{csclPstpReleCn}
  , cscl_pstp_rele_yn = 'Y'
  , last_chpr_id = #{lastChprId}
  , last_chg_dttm = SYSTIMESTAMP
WHERE dtl_dcsh_no = #{dtlDcshNo}
  AND last_yn = 'Y'
  AND cscl_pstp_rele_yn = 'N'  /** 통관보류해제여부 */
