SELECT DTL_DCSH_NO , REFF_NO , MDFY_DGCNT , dclr_cstm_cd , EPC_CD , TO_CHAR(TO_DATE(DCLR_DT, 'YYYYMMDD'),'DD/MM/YYYY') AS DCLR_DT , TO_CHAR(TO_DATE(RQST_DT, 'YYYYMMDD'),'DD/MM/YYYY') AS RQST_DT , DCLR_PT_CD , CSCL_PLN_CD , DCSH_FM_CD , INVC_NO , BL_NO , DCER_CD FROM ( SELECT DTL_DCSH_NO , REFF_NO , MDFY_DGCNT , dclr_cstm_cd , EPC_CD , DCLR_DT , RQST_DT , DCLR_PT_CD , CSCL_PLN_CD , DCSH_FM_CD , INVC_NO , BL_NO , DCER_CD FROM TB_CLRI_DED_COMN A WHERE DEL_YN = 'N' AND (REFF_NO IS NULL OR REFF_NO = '') AND PRCS_STTS_CD NOT IN ('A01','G03','G04')
AND DCER_CD = #{dcerCd}
AND DCLR_DT >= TO_CHAR(TO_DATE(#{searchKeywordFrom}, 'DD/MM/YYYY'),'YYYYMMDD')
AND DCLR_DT <= TO_CHAR(TO_DATE(#{searchKeywordTo}, 'DD/MM/YYYY'),'YYYYMMDD')
AND DCLR_PT_CD = #{dclrPtCd}
AND DTL_DCSH_NO LIKE #{dtlDcshNo} || '%'
AND SUBSTR(EPC_CD, 0, 2)= SUBSTR(#{epcCd}, 0, 2)
AND PRCS_STTS_CD = 'D02' /** 수정가능 조건 : 수신완료, 세관배부, 심사자배부, 부서배부, 선별완료, 취소접수, 취소완료, 직권취소 제외 */ AND CSCL_PSTP_YN = 'Y'
/* !== 담당자 ==!*/
AND A.AUDT_EMP_ID = #{userId}
/* !== 관리자 ==!*/
/* !== 부서장 ==!*/ AND A.DEPT_CD = #{userDeptCd} AND A.DCLR_CSTM_CD = #{userCstmCd}
/* !== 세관장 ==!*/ AND A.DCLR_CSTM_CD = #{userCstmCd}
ORDER BY DCLR_DT DESC, FRST_RGSR_DTTM DESC )

