       ( (T2.HS_EXCP_MTR_CD IS NULL OR T2.HS_EXCP_MTR_CD = '') OR(T2.HS_EXCP_MTR_CD IS NOT NULL AND T2.HS_EXCP_MTR_CD != '' AND T2.HS_EXCP_MTR_CD = #{hsExcpMtrCd}) )
       )
   UNION ALL
