mapper.select:
/* selectPscCaplConsList */
SELECT A.CAPL_CONS_NO /** @TODO : 번역필요 !== 자본위탁번호 ==! */ , A.CONS_CSTM_CD /** @TODO : 번역필요 !== 위탁세관코드 ==! */ , FN_GET_ORGN_NM(A.CONS_CSTM_CD) AS CONS_CSTM_CD_NM /** !== 세관명 ==! */ , TO_CHAR(TO_DATE(A.CONS_DT, 'YYYYMMDD'), 'DD/MM/YYYY') AS CONS_DT /** @TODO : 번역필요 !== 위탁일자 ==! */ , A.CAPL_CONS_STTS_CD /** @TODO : 번역필요 !== 자본위탁상태코드 ==! */ , A.CONSR_NTNAL_IDFY_NO /** @TODO : 번역필요 !== 위탁자자국민식별번호 ==! */ , A.CONSR_PSPR_NO /** @TODO : 번역필요 !== 위탁자여권번호 ==! */ , A.APRV_PRCS_STTS_CD /** Code De Statut Du Traitement D'Approbation !== 결재처리상태코드 ==! */ FROM ALPASSINT.TB_PSCI_CAPL_CONS_CRPP A WHERE 1=1 AND DEL_YN = 'N'
AND A.CAPL_CONS_NO = #{caplConsNo} /** @TODO : 번역필요 !== 자본위탁번호 ==! */
AND A.CONS_DT BETWEEN TO_CHAR(TO_DATE(REPLACE(#{consDtFrom}, '/', ''), 'DDMMYYYY'), 'YYYYMMDD') AND TO_CHAR(TO_DATE(REPLACE(#{consDtTo}, '/', ''), 'DDMMYYYY'), 'YYYYMMDD')
AND A.CAPL_CONS_STTS_CD = #{caplConsSttsCd}
AND A.CONSR_NTNAL_IDFY_NO = #{consrNtnalIdfyNo}
AND CONSR_PSPR_NO = #{consrPsprNo}
ORDER BY FRST_RGSR_DTTM DESC

mapper.update:
/* updatePscCaplCons */ UPDATE ALPASSINT.TB_PSCI_CAPL_CONS_CRPP SET CONS_CSTM_CD = #{consCstmCd} /** @TODO : 번역필요 !== 위탁세관코드 ==! */ , CONS_AMT = #{consAmt} /** Montant de dépôt !== 위탁금액 ==! */ , CONS_DT = #{consDt} /** @TODO : 번역필요 !== 위탁일자 ==! */ , CAPL_CONS_STTS_CD = #{caplConsSttsCd} /** @TODO : 번역필요 !== 자본위탁상태코드 ==! */
, WDRW_CSTM_CD = #{wdrwCstmCd} /** @TODO : 번역필요 !== 회수세관 ==! */ , WDRW_DT = #{wdrwDt} /** Date De Récupération !== 회수일자 ==! */ , CONSR_FMNM = #{consrFmnm} /** @TODO : 번역필요 !== 위탁자성 ==! */ , CONSR_GVNM_NM = #{consrGvnmNm} /** @TODO : 번역필요 !== 위탁자이름명 ==! */ , CONSR_ADDR = #{consrAddr} /** @TODO : 번역필요 !== 위탁자주소 ==! */ , CONSR_NTNAL_IDFY_NO = #{consrNtnalIdfyNo} /** @TODO : 번역필요 !== 위탁자자국민식별번호 ==! */ , CONSR_PSPR_NO = #{consrPsprNo} /** @TODO : 번역필요 !== 위탁자여권번호 ==! */ , PSPR_NO_ISS_DT = #{psprNoIssDt} /** @TODO : 번역필요 !== 여권발급일자 ==! */ , PSPR_NO_ISS_ITT = #{psprNoIssItt} /** @TODO : 번역필요 !== 여권발급기관 ==! */ , MNDR_FMNM = #{mndrFmnm} /** @TODO : 번역필요 !== 위임자성 ==! */ , MNDR_GVNM_NM = #{mndrGvnmNm} /** @TODO : 번역필요 !== 위임자이름명 ==! */ , MNDR_ADDR = #{mndrAddr} /** @TODO : 번역필요 !== 위임자주소 ==! */ , MNDR_NTNAL_IDFY_NO = #{mndrNtnalIdfyNo} /** @TODO : 번역필요 !== 위임자자국민식별번호 ==! */ , REFF_NO = #{reffNo} /** N° De Référence !== 참조번호 ==! */
, MNDR_PSPR_NO = #{mndrPsprNo} /** N° de passeport de mendataire !== 위임자여권번호 ==! */ , LAST_CHPR_ID = #{lastChprId} /** Id Du Modificateur Final !== 최종변경자ID ==! */ , LAST_CHG_DTTM = SYSTIMESTAMP /** Date Et Heure De Modification Finale !== 최종변경일시 ==! */ WHERE DEL_YN = 'N' /** Suppression ON !== 삭제여부 ==! */ AND CAPL_CONS_NO = #{caplConsNo} /** @TODO : 번역필요 !== 자본위탁번호 ==! */
