/* selectBfInfo */ SELECT * FROM ( SELECT DRVR_GVNM_NM , DRVR_FMNM , DRVR_BRDY , DRVR_BIRT_REGN_NM , DRVR_PSPR_NO , DRVR_PSPR_VALD_DT , DECODE(DRVR_ESTM_YN,'Y','Y','N') AS DRVR_ESTM_YN , DRVR_JOB_NM , DRVR_PT_CD , DRVR_ADDR , DRVR_OVRS_ADDR , DRVR_PSPR_NO_ISS_DT , DRVR_PSPR_NO_ISS_ITT , DRVR_NAT_CD , DRVR_FAR_SNM , DRVR_MOR_SNM , NTNAL_IDFY_NO , 1 AS GUBUN , FRST_RGSR_DTTM FROM TB_PSCI_TPD_PSNR_BSCS WHERE DRVR_PSPR_NO = #{psprNo}
UNION ALL
SELECT PSNR_FMNM AS DRVR_FMNM , PSNR_GVNM_NM AS DRVR_GVNM_NM , BRDY AS DRVR_BRDY , BIRT_REGN_NM AS DRVR_BIRT_REGN_NM , PSPR_NO AS DRVR_PSPR_NO , PSPR_VALD_DT AS DRVR_PSPR_VALD_DT , DECODE(ESTM_YN,'Y','Y','N') AS DRVR_ESTM_YN , JOB_NM AS DRVR_JOB_NM , PSNR_PT_CD AS DRVR_PT_CD , DZA_ADDR AS DRVR_ADDR , OVRS_RSDN_ADDR AS DRVR_OVRS_ADDR , PSPR_NO_ISS_DT AS DRVR_PSPR_NO_ISS_DT , PSPR_NO_ISS_ITT AS DRVR_PSPR_NO_ISS_ITT , NAT_CD AS DRVR_NAT_CD , '' AS DRVR_FAR_SNM , '' AS DRVR_MOR_SNM , '' AS NTNAL_IDFY_NO , 2 AS GUBUN , FRST_RGSR_DTTM FROM TB_PSCI_PSNR_BLGG_IPRT WHERE PSPR_NO = #{psprNo}
UNION ALL SELECT PSNR_FMNM AS DRVR_FMNM , PSNR_GVNM_NM AS DRVR_GVNM_NM , BRDY AS DRVR_BRDY , BIRT_REGN_NM AS DRVR_BIRT_REGN_NM , PSPR_NO AS DRVR_PSPR_NO , '' AS DRVR_PSPR_VALD_DT , DECODE(ESTM_YN,'Y','Y','N') AS DRVR_ESTM_YN , JOB_NM AS DRVR_JOB_NM , PSNR_PT_CD AS DRVR_PT_CD , DZA_ADDR AS DRVR_ADDR , OVRS_RSDN_ADDR AS DRVR_OVRS_ADDR , PSPR_NO_ISS_DT AS DRVR_PSPR_NO_ISS_DT , PSPR_NO_ISS_ITT AS DRVR_PSPR_NO_ISS_ITT , NAT_CD AS DRVR_NAT_CD , '' AS DRVR_FAR_SNM , '' AS DRVR_MOR_SNM , NTNAL_IDFY_NO , 3 AS GUBUN , FRST_RGSR_DTTM FROM TB_PSCI_FRXCG_DCSH_MGMT WHERE PSPR_NO = #{psprNo}
) ORDER BY GUBUN , FRST_RGSR_DTTM DESC LIMIT 1
