mapper.select:
/* ClriIbnVrfcBsop_selectEpcCdByDclrPtCd */
SELECT epc_cd
FROM tb_clri_epc_cd
WHERE del_yn = 'N'
  AND dclr_pt_cd = #{dclrPtCd}
  AND epc_cd = #{epcCd}
  AND TO_CHAR(CURRENT_DATE,'YYYYMMDD') BETWEEN EPC_APLY_STRT_DT AND EPC_APLY_END_DT

/* ClriIbnVrfcBsop_selectCsclPlnCdByDclrPtCd */
SELECT t2.cscl_pln_cd
FROM tb_clri_epc_cd t1
   , tb_clri_ded_vrfc t2
WHERE t1.del_yn = 'N'
  AND t1.dclr_pt_cd = #{dclrPtCd}
  AND t1.epc_cd = #{epcCd}
  AND t1.epc_cd = t2.epc_cd
  AND t2.cscl_pln_cd = #{csclPlnCd}
  AND t2.del_yn = 'N'
  AND TO_CHAR(CURRENT_DATE,'YYYYMMDD') BETWEEN t1.EPC_APLY_STRT_DT AND t1.EPC_APLY_END_DT

/* ClriIbnVrfcBsop_selectDubleCheckCntrNo */ SELECT C.CNTR_SRNO, A.DTL_DCSH_NO, B.CNTR_NO FROM TB_CLRI_DED_COMN A, TB_CLRI_DED_CNTR B, TB_CLRI_DED_CNTR C WHERE A.DTL_DCSH_NO = B.DTL_DCSH_NO AND A.PRCS_STTS_CD NOT IN ('G03', 'G04') /* 취소건 제외 */ AND A.CAG_MGMT_NO = #{cagMgmtNo}
AND A.CMDT_STGE_LOCT_CD = #{cmdtStgeLoctCd}
AND A.CMDT_STGE_LOCT_CD IS NULL
AND A.DTL_DCSH_NO <> #{dtlDcshNo} AND A.DEL_YN = 'N' AND B.DEL_YN = 'N' AND B.CNTR_NO = C.CNTR_NO AND C.DTL_DCSH_NO = #{dtlDcshNo} AND C.DEL_YN = 'N'

/* ClriIbnVrfcBsop_selectCntrPckgTtwgInfo */
SELECT NVL(SUM(A.PCKG_GCNT), 0) AS PCKG_GCNT,   /* BL 컨테이너 합계(총포장수량) */
       NVL(SUM(A.TTWG), 0) AS TTWG              /* BL 컨테이너 합계(총중량) */
FROM tb_cari_cag_dcsh_bl_cntr A,
     TB_CLRI_DED_CNTR B
WHERE A.CAG_MGMT_NO = #{cagMgmtNo}
  AND A.DEL_YN      = 'N'
  AND A.CNTR_NO     = B.CNTR_NO
  AND B.DTL_DCSH_NO      = #{dtlDcshNo}
  AND B.DEL_YN      = 'N'

/* selectCo */
SELECT NIF
FROM TB_POTE_NIF
WHERE DEL_YN = 'N'
  AND NIF_STTS_CD != '0'
  AND NIF = #{imExBucnNo}

/* ClriIbnVrfcBsop_selectIndvMgUseCnt */
SELECT COUNT(*)
FROM TB_CLRI_DED_COMN A, TB_CLRI_DED_MG B
WHERE A.DEL_YN = 'N'
AND B.DEL_YN = 'N'
AND A.DTL_DCSH_NO = B.DTL_DCSH_NO


   AND A.DTL_DCSH_NO <> #{dtlDcshNo}


AND A.PRCS_STTS_CD NOT IN ('G03', 'G04')
AND B.MG_AC_NO = #{mgAcNo}

/* ClriIbnVrfcBsop_selectDedDclrStatsOfLoc */ SELECT COUNT(*) AS PRE_DEC_CNT /* 이전신고 건수 */ , NVL(SUM(DECODE(DCRD_YN, 'Y', 1, 0)), 0) AS PRE_DEC_DCRD_Y_CNT /* 차상반출 Y 신고 건수 */ , NVL(SUM(DECODE(DCRD_YN, 'N', 1, 0)), 0) AS PRE_DEC_DCRD_N_CNT /* 차상반출 N 신고 건수 */ , NVL(SUM(DECODE(DIVD_CSCL_TP_CD, 'A', 1, 0)), 0) AS PRE_DEC_A_CNT /* 분할통관구분 A|전체 신고 건수 */ , NVL(SUM(DECODE(DIVD_CSCL_TP_CD, 'I', 1, 0)), 0) AS PRE_DEC_I_CNT /* 분할통관구분 I|분할(시작) 신고 건수 */ , NVL(SUM(DECODE(DIVD_CSCL_TP_CD, 'D', 1, 0)), 0) AS PRE_DEC_D_CNT /* 분할통관구분 D|분할(진행중) 건수 */ , NVL(SUM(DECODE(DIVD_CSCL_TP_CD, 'L', 1, 0)), 0) AS PRE_DEC_L_CNT /* 분할통관구분 L|분할(최종) 건수 */ FROM TB_CLRI_DED_COMN WHERE DEL_YN = 'N' AND CAG_MGMT_NO = #{cagMgmtNo}
AND CMDT_STGE_LOCT_CD = #{cmdtStgeLoctCd} /* 차상반출이 아닌 경우는 무조건 물품장치장소 값을 비교해야 함. 다만 적하목록하역전신고(BMD)인 경우는 물품위치를 체크하지 않음. (2020.01.24) */ AND ( /* 물품위치장소랑 보세구역창고랑 같은것 또는 반출한신고서 물품위치장소랑 보세구역창고랑 같은것 - 20190530 추가*/ ( NVL(CMDT_STGE_LOCT_CD, ' ') <> NVL(BNBN_CMDT_WRHS_CD, ' ') ) OR ( NVL(CMDT_STGE_LOCT_CD, ' ') = NVL(BNBN_CMDT_WRHS_CD, ' ') AND PRCS_STTS_CD <> 'D07' ) )
AND PRCS_STTS_CD NOT IN ('G03', 'G04') /* 취소건 제외 */
AND DTL_DCSH_NO <> #{dtlDcshNo} /* 현재 신고번호가 존재하는 경우는 본인 신고번호 제외하고 판단 (제출전) 이라면 해당 조건은 비교하지 않음*/ AND FRST_RGSR_DTTM < ( SELECT FRST_RGSR_DTTM FROM TB_CLRI_DED_COMN WHERE DTL_DCSH_NO = #{dtlDcshNo} ) /* 분할통관건이 동시에 진행 중인 경우를 위한 처리를 위함. */

/* ClriIbnVrfcBsop_selectCagInvnDcrdY */ SELECT A.CAG_MGMT_NO , A.BNBN_WRHS_CD , A.BRNG_PCKG_GCNT , A.BRNG_WGHT , A.RLSE_PCKG_GCNT + NVL(C.PCKG_TGCNT, 0) AS RLSE_PCKG_GCNT /* SAD는 실제 반출은 아니고 서류상 반출 정보임,화물시스템 근거로 반출은 실반출임. */ , A.RLSE_WGHT + NVL(C.AL_TTWG, 0) AS RLSE_WGHT /* SAD는 실제 반출은 아니고 서류상 반출 정보임,화물시스템 근거로 반출은 실반출임. */ , A.BRNG_PCKG_GCNT - ( A.RLSE_PCKG_GCNT + NVL(C.PCKG_TGCNT, 0) ) AS INVN_PCKG_GCNT /* SAD에서 정정시 사용 가능한 재고 */ , A.BRNG_WGHT - ( A.RLSE_WGHT + NVL(C.AL_TTWG, 0) ) AS INVN_WGHT /* SAD에서 정정시 사용 가능한 재고 */ , 0 AS DED_PCKG_GCNT /* 정정신청서로 실제 반출한 반출량 즉 해당 반출량 이하로는 정정할 수 없음 */ , 0 AS DED_WGHT /* 정정신청서로 실제 반출한 반출량 즉 해당 반출량 이하로는 정정할 수 없음 */ , NVL(C.PCKG_TGCNT, 0) AS PCKG_TGCNT /* SAD 이전 신고 포장수량 합계 */ , NVL(C.AL_TTWG, 0) AS AL_TTWG /* SAD 이전 신고 총중량 합계 */ FROM ( /*****************************************************************************/ /* 차상반출인 경우 B/L로 포장개수와 중량을 재고로 판단함 */ /*****************************************************************************/ SELECT B.CAG_MGMT_NO /** CRN !==화물관리번호==! */ , '' AS BNBN_WRHS_CD /** 보세창고코드 */ , B.PCKG_GCNT AS BRNG_PCKG_GCNT /** Nombre de colis !==포장개수==! */ , DECODE(B.CAG_CHRC_CD, '8', B.LIR_VOL, B.TTWG) AS BRNG_WGHT /** Poids brut !==총중량==! */ , 0 AS RLSE_PCKG_GCNT , 0 AS RLSE_WGHT FROM tb_cari_cag_dcsh M, tb_cari_cag_dcsh_bl B, TB_CARI_CAG_PRCS_BRKD P WHERE 1=1 AND B.CAG_MGMT_NO = #{cagMgmtNo} /* 화물관리번호 */ AND M.DEL_YN = 'N' AND B.DEL_YN = 'N' AND B.CAG_TP_CD IN ('IM', 'TR') /* 화물구분코드 : IM:수입화물, TR:Transit화물 */ AND ( B.BL_PT_CD = 'H' /* BL유형코드 : H:House B/L (AWB) */ OR ( B.BL_PT_CD = 'M' /* BL유형코드 : M:B/L */ AND B.BL_TP_CD IN ('S', 'C') /* BL구분코드 : S:Simple, C:Creation du N° de gestion de cargaison */ ) ) AND M.MRN = B.MRN AND M.CAG_RQST_TP_CD = P.CAG_RQST_TP_CD AND M.MRN = P.cag_rqst_no AND P.PRCS_STTS_CD = 'D06' ) A, ( /*********************************************************************/ /* 해당화물관리번호로 유효한 이전 SAD건의 포장수량, 총중량 합계 계산 */ /*********************************************************************/ SELECT CAG_MGMT_NO , '' AS BNBN_WRHS_CD , SUM(PCKG_TGCNT) AS PCKG_TGCNT , SUM(AL_TTWG) AS AL_TTWG FROM TB_CLRI_DED_COMN WHERE CAG_MGMT_NO = #{cagMgmtNo} /* 화물관리번호 */ AND DEL_YN = 'N' /* 삭제여부 */ AND PRCS_STTS_CD NOT IN ('G03', 'G04') /* 취소건 제외 */
AND DTL_DCSH_NO <> #{dtlDcshNo} /* 현재 수정신청번호는 제외 */
GROUP BY CAG_MGMT_NO ) C WHERE A.CAG_MGMT_NO = C.CAG_MGMT_NO(+)

/* ClriIbnVrfcBsop_selectCagInvnDcrdN */ SELECT A.CAG_MGMT_NO , A.ibobz_cd as BNBN_WRHS_CD , A.BRNG_PCKG_GCNT , A.BRNG_WGHT , (A.RLSE_PCKG_GCNT - NVL(B.DED_ALL_PCKG_GCNT, 0)) + NVL(C.PCKG_TGCNT, 0) AS RLSE_PCKG_GCNT /* SAD는 실제 반출은 아니고 서류상 반출 정보임,화물시스템 근거로 반출은 실반출임. */ , (A.RLSE_WGHT - NVL(B.DED_ALL_WGHT, 0)) + NVL(C.AL_TTWG, 0) AS RLSE_WGHT /* SAD는 실제 반출은 아니고 서류상 반출 정보임,화물시스템 근거로 반출은 실반출임. */ , A.BRNG_PCKG_GCNT - ( (A.RLSE_PCKG_GCNT - NVL(B.DED_ALL_PCKG_GCNT, 0)) + NVL(C.PCKG_TGCNT, 0) ) AS INVN_PCKG_GCNT /* SAD에서 정정시 사용 가능한 재고 */ , A.BRNG_WGHT - ( (A.RLSE_WGHT - NVL(B.DED_ALL_WGHT, 0)) + NVL(C.AL_TTWG, 0) ) AS INVN_WGHT /* SAD에서 정정시 사용 가능한 재고 */ , NVL(B.DED_PCKG_GCNT, 0) AS DED_PCKG_GCNT /* 정정신청서로 실제 반출한 반출량 즉 해당 반출량 이하로는 정정할 수 없음 */ , NVL(B.DED_WGHT, 0) AS DED_WGHT /* 정정신청서로 실제 반출한 반출량 즉 해당 반출량 이하로는 정정할 수 없음 */ , NVL(C.PCKG_TGCNT, 0) AS PCKG_TGCNT /* SAD 이전 신고 포장수량 합계 */ , NVL(C.AL_TTWG, 0) AS AL_TTWG /* SAD 이전 신고 총중량 합계 */ FROM ( /**********************************************************/ /* 화물관리번호로B/L 단위 반입포장개수와 반입중량을 조회함*/ /**********************************************************/ SELECT CAG_MGMT_NO /* 화물관리번호 */ , ibobz_cd /* 보세창고코드 */ , SUM(BRNG_PCKG_GCNT) AS BRNG_PCKG_GCNT /* 반입포장개수 */ , SUM(BRNG_WGHT) AS BRNG_WGHT /* 반입중량 */ , SUM(RLSE_PCKG_GCNT) AS RLSE_PCKG_GCNT /* 반출포장개수 */ , SUM(RLSE_WGHT) AS RLSE_WGHT /* 반출중량 */ FROM TB_CARI_INVN /* CARI_재고 */ WHERE CAG_MGMT_NO = #{cagMgmtNo} /* 화물관리번호 */ AND ibobz_cd = #{cmdtStgeLoctCd} /* 보세창고코드 */ /* AND INVN_YN = 'Y' 반출후 즉 재고 모두 소진 후도 정정 가능하도록 조건 제거 함(2020.05.27) */ AND DEL_YN = 'N' GROUP BY CAG_MGMT_NO, ibobz_cd ) A, ( /**************************************************************/ /* 반출근거가 SAD로 반출된 량을 조회함 */ /**************************************************************/ SELECT X.CAG_MGMT_NO /* 화물관리번호 */ , X.ibobz_cd /* 보세창고코드 */ , SUM(DECODE(NVL(Y.RELA_NO, X.RELA_NO), #{dtlDcshNo}, X.RLBR_PCKG_GCNT, 0)) AS DED_PCKG_GCNT /* SAD 수정 건의 반출포장개수 */ , SUM(DECODE(NVL(Y.RELA_NO, X.RELA_NO), #{dtlDcshNo}, X.RLBR_WGHT, 0)) AS DED_WGHT /* SAD 수정 건의 반출중량 */ , SUM(X.RLBR_PCKG_GCNT) AS DED_ALL_PCKG_GCNT /* SAD로 반출한 반출포장개수 합계*/ , SUM(X.RLBR_WGHT) AS DED_ALL_WGHT /* SAD로 반출한 합계 반출중량 합계 */ FROM TB_CARI_RLBR X /* CARI_반출입 */ , TB_CARI_TRNP Y /* CARI_운송 (+) */ WHERE X.CAG_MGMT_NO = #{cagMgmtNo} /* 화물관리번호 */ AND X.ibobz_cd = #{cmdtStgeLoctCd} /* 보세창고코드 */ AND X.RLBR_TP_CD = 'O' /* 반출입구분코드: O|반출 */ AND X.RLBR_PT_CD IN ('50', '51', '52', '60', '61') /* 반출입유형코드 : '50', '51', '52', '60', '61' */ AND X.DEL_YN = 'N' AND X.RELA_NO = Y.TRNP_RQST_NO(+) AND Y.DEL_YN(+) = 'N' AND EXISTS (SELECT 1 FROM TB_CARI_CAG_PRCS_BRKD WHERE DEL_YN = 'N' AND PRCS_STTS_CD IN ('D08', 'D10') AND cag_rqst_no = X.rlbr_rqst_no) /* 처리상테 : 'D08', 'D10' (처리상태가 승인 혹은 자동승인인 건만 조회)*/ GROUP BY X.CAG_MGMT_NO, X.ibobz_cd ) B, ( /**********************************************************************/ /* 해당화물관리번호로 유효한 이전 SAD건의 포장수량, 총중량 합계 계산 */ /**********************************************************************/ SELECT CAG_MGMT_NO , CMDT_STGE_LOCT_CD AS BNBN_WRHS_CD , SUM(PCKG_TGCNT) AS PCKG_TGCNT , SUM(AL_TTWG) AS AL_TTWG FROM TB_CLRI_DED_COMN WHERE CAG_MGMT_NO = #{cagMgmtNo} /* 화물관리번호 */ AND CMDT_STGE_LOCT_CD = #{cmdtStgeLoctCd} /* 보세창고코드 */ AND ( /* 물품위치장소랑 보세구역창고랑 같은것 또는 반출한신고서 물품위치장소랑 보세구역창고랑 같은것 - 20190530 추가*/ ( NVL(CMDT_STGE_LOCT_CD, ' ') <> NVL(BNBN_CMDT_WRHS_CD, ' ') ) OR ( NVL(CMDT_STGE_LOCT_CD, ' ') = NVL(BNBN_CMDT_WRHS_CD, ' ') AND PRCS_STTS_CD <> 'D07' ) ) AND DEL_YN = 'N' /* 삭제여부 */ AND PRCS_STTS_CD NOT IN ('G03', 'G04') /* 취소건 제외 */
AND DTL_DCSH_NO <> #{dtlDcshNo} /* 현재 수정신청번호는 제외 */
GROUP BY CAG_MGMT_NO, CMDT_STGE_LOCT_CD ) C WHERE A.CAG_MGMT_NO = B.CAG_MGMT_NO(+) AND A.ibobz_cd = B.ibobz_cd(+) AND A.CAG_MGMT_NO = C.CAG_MGMT_NO(+) AND A.ibobz_cd = C.BNBN_WRHS_CD(+)

/* ClriIbnVrfcBsop_selectBfSadNoNotEqCheck */
SELECT 'Y' FROM (
                    SELECT A.DTL_DCSH_NO, A.BF_DTL_DCSH_NO
                    FROM TB_CLRI_DED_PDLS A
                    WHERE A.DTL_DCSH_NO     = #{dtlDcshNo}
                      AND A.DEL_YN     = 'N'
                    GROUP BY A.DTL_DCSH_NO, A.BF_DTL_DCSH_NO
                )
GROUP BY DTL_DCSH_NO HAVING COUNT(*)  >  1

/* ClriIbnVrfcBsop_selectMfcmClsfSpelCd */
SELECT cmdt_clsf_spel_cd
FROM tb_clri_mfcm_clsf_spel_cd
WHERE del_yn = 'N'
  AND use_yn = 'Y'
  AND mafc_co_nif = #{mafcCoNif}
  AND cmdt_clsf_spel_cd = #{cmdtClsfSpelCd}
  AND hs_cd = #{hsCd}

        /* ClriIbnVrfcBsop_selectQtaApreCnvnPrcsQty */

        SELECT NVL(SUM(t2.qty),0)
		  FROM tb_clri_ded_comn t1
             , tb_clri_ded_pdls t2
		 WHERE t1.del_yn = 'N'



           AND t1.dtl_dcsh_no <> #{dtlDcshNo}



           AND t1.dtl_dcsh_no = t2.dtl_dcsh_no
           AND t2.del_yn = 'N'
           AND t2.qta_apre_no = #{exmpDcCd}
           AND t2.hs_cd BETWEEN #{fromHsCd} AND #{toHsCd}
           AND t1.prcs_stts_cd IN ('A04','B01','C02','B04','B02','D01','D02','D03','H04','E02','D04','F01','F02','G01','G02')  /** 반출전 처리상태 */

        /* ClriIbnVrfcBsop_selectMgdcAprePrcsQty */

        SELECT NVL(SUM(t2.qty),0)
		  FROM tb_clri_ded_comn t1
             , tb_clri_ded_pdls t2
		 WHERE t1.del_yn = 'N'



           AND t1.dtl_dcsh_no <> #{dtlDcshNo}



           AND t1.dtl_dcsh_no = t2.dtl_dcsh_no
           AND t2.del_yn = 'N'
           AND t2.mg_dscn_apre_no = #{exmpDcCd}
           AND t2.hs_cd = #{hsCd}
           AND t1.prcs_stts_cd IN ('A04','B01','C02','B04','B02','D01','D02','D03','H04','E02','D04','F01','F02','G01','G02')  /** 반출전 처리상태 */


		/* ClriIbnVrfcBsop_selectBfPsmgtPdlsPrcsQty */
        SELECT NVL(SUM(t2.qty),0)
		  FROM tb_clri_ded_comn t1
             , tb_clri_ded_pdls t2
		 WHERE t1.del_yn = 'N'



           AND t1.dtl_dcsh_no <> #{dtlDcshNo}



           AND t1.dtl_dcsh_no = t2.dtl_dcsh_no
           AND t2.del_yn = 'N'
           AND t2.bf_dtl_dcsh_no = #{bfDtlDcshNo}
           AND t2.bf_pdls_no = #{bfPdlsNo}
           AND t1.prcs_stts_cd IN ('A04','B01','C02','B04','B02','D01','D02','D03','H04','E02','D04','F01','F02','G01','G02')  /** 반출전 처리상태 */


            and (   (t1.dclr_pt_cd in ('IM4', 'IM5', 'IM6', 'IM7', 'END', 'TRC', 'LOI', 'ZFI', 'ZFP') and t1.imppn_idfy_no = #{nif})
            or (t1.dclr_pt_cd in ('EX1', 'EX2', 'EX3', 'LOE', 'ZFE') and t1.exppn_idfy_no = #{nif}))

/* ClriIbnVrfcBsop_selectMafcRwmsPrcsQty */ SELECT NVL(SUM(t3.qty),0) FROM tb_clri_ded_comn t1 , tb_clri_ded_pdls t2 , tb_clri_ded_rwms t3 WHERE t1.del_yn = 'N' AND t1.prcs_stts_cd IN ('A04','B01','C02','B04','B02','D01','D02','D03','H04','E02','D04','F01','F02','G01','G02') /** 반출전 처리상태 */
AND t1.dtl_dcsh_no <> #{dtlDcshNo}
AND t1.dtl_dcsh_no = t2.dtl_dcsh_no AND T2.del_yn = 'N' AND t2.dtl_dcsh_no = t3.dtl_dcsh_no AND t2.pdls_no = t3.pdls_no AND T3.del_yn = 'N' AND t3.bf_dtl_dcsh_no = #{bfDtlDcshNo} AND t3.bf_pdls_no = #{bfPdlsNo}

/* selectCurrPrcAudtDgcnt */
SELECT PRC_EV_DGCNT
FROM TB_CLRI_DED_VLUT_AUDT_COMN
WHERE DEL_YN = 'N'
  AND dtl_dcsh_no = #{dtlDcshNo}
  AND LAST_YN = 'Y'

/* ClriIbnVrfcBsop_selectDedComnDuplCagMgmtNo */
SELECT COUNT(*)
FROM tb_clri_ded_comn
WHERE del_yn = 'N'
  AND prcs_stts_cd NOT IN ('G03','G04')  /** 취소 처리상태 */
  AND cag_mgmt_no = #{cagMgmtNo}

/* ClriIbnVrfcBsop_selectEqBlSadSumInfo */
SELECT NVL(SUM(pdls_gcnt),0) AS sum_pdls_gcnt
, NVL(SUM(pckg_tgcnt),0) AS sum_pckg_tgcnt
, NVL(SUM(al_ttwg),0) AS sum_al_ttwg
, NVL(SUM(al_ntwg),0) AS sum_al_ntwg
FROM tb_clri_ded_comn
WHERE prcs_stts_cd IN ('A04','B01','C02','B04','B02','D01','D02','D03','H04','E02','D04','F01','F02','G01','G02')  /** 반출전 처리상태 */


        AND cag_mgmt_no = #{cagMgmtNo}


        AND bl_no = #{blNo}
        AND arvl_dt = #{arvlDt}

/* ClriIbnVrfcBsop_selectImppnIdfyNo */
SELECT imppn_idfy_no
FROM tb_clri_ded_comn
WHERE del_yn = 'N'
  AND dtl_dcsh_no = #{bfSadNo}
  AND imppn_idfy_no = #{imExBucnNo}

/* ClriIbnVrfcBsop_selectBfPdls */
SELECT QTY
FROM TB_CLRI_DED_PDLS
WHERE DEL_YN = 'N'
  AND DTL_DCSH_NO = #{bfDtlDcshNo}
  AND PDLS_NO = #{bfPdlsNo}

/* ClriIbnVrfcBsop_selectBfPdlsMdlStsz */
SELECT SUM(NVL(INVC_AMT, 0)) AS INVC_AMT
FROM TB_CLRI_DED_MDL_STSZ
WHERE DEL_YN = 'N'
  AND DTL_DCSH_NO = #{bfDtlDcshNo}
  AND PDLS_NO = #{bfPdlsNo}

/* selectDedVrfcClusMeta */
SELECT t1.trgt_tbl_nm
, t1.trgt_col_nm
, t2.lbl_id
FROM tb_clri_ded_vrfc_base t1
, tb_com_lbl t2
, tb_com_lbl_lang t3
WHERE t1.del_yn = 'N'
AND (t1.esnt_inp_vrfc_yn = 'Y'

    #{item,jdbcType=VARCHAR}

)
AND t1.epc_cd = #{epcCd}
AND t1.use_yn = 'Y'
AND t1.trgt_tbl_nm = #{trgtTblNm}

    #{item,jdbcType=VARCHAR}

AND t1.lbl_id = t2.lbl_id(+)
AND t2.del_yn(+) = 'N'
AND t2.use_yn(+) = 'Y'
AND t2.lbl_id = t3.lbl_id(+)
AND t3.lang_cd(+) = #{langCd}
AND t3.del_yn(+) = 'N'

SELECT ${errKeyCol1}'' AS erky1_val
, ${errKeyCol2}'' AS erky2_val
, ${errKeyCol3}'' AS erky3_val
, ${trgtColNm}
FROM ${trgtTblNm}
WHERE dtl_dcsh_no = #{dtlDcshNo}
AND del_yn = 'N'

/** ClriIbnVrfcBsop_selectBusiComRegnCd */
SELECT REGN_CD, REGN_TP_CD, CNTY_CD
FROM TB_COM_REGN_CD
WHERE DEL_YN = 'N'

    AND CNTY_CD = #{cntyCd}


    AND REGN_TP_CD = #{regnTpCd}


    AND REGN_CD = #{regnCd}

/* ClriIbnVrfcBsop_selectWhrsNIU */
select ibobz_nif
from TB_CARI_IBOBZ
where ibobz_co_cd = #{bnbnCmdtWrhsCd}
and DEL_YN     = 'N'
and ibobz_tp_cd <> '01'; /* 1 : 공공, 2:사설, 3: 특별 */
