mapper.select:
/* selectAeoComWriopList */
SELECT A.WRIOP_NO /** N° Feuille D'Avis !== 의견서번호 ==! */ , A.ATHR_RQST_TP_CD /** Code de type de demande d'agrément !== 공인신청구분코드 ==! */ , FN_GET_CD_VDVAL_NM('AEO_0004', A.ATHR_RQST_TP_CD, #{langCd}) AS ATHR_RQST_TP_NM /** Code de type de demande d'agrément !== 공인신청구분코드 ==! */ , A.AEO_RQST_NO /** Numéro de demande du statut OEA !== AEO신청번호 ==! */ , A.AEO_ATHR_NO /** Numéro d'OEA !== AEO공인번호 ==! */ , A.PRCS_STTS_CD /** Code De Statut De Traitement !== 처리상태코드 ==! */ , FN_GET_CD_VDVAL_NM((CASE A.ATHR_RQST_TP_CD WHEN 'ARQ' THEN 'AEO_0015' ELSE 'AEO_0016' END), A.PRCS_STTS_CD, #{langCd}) AS PRCS_STTS_NM /** Code De Statut De Traitement !== 처리상태코드 ==! */ , A.DTL_PRCS_STTS_CD /** Code d'état détaillé du traitement !== 상세처리상태코드 ==! */ , FN_GET_CD_VDVAL_NM((CASE A.ATHR_RQST_TP_CD WHEN 'ARQ' THEN 'AEO_0017' ELSE 'AEO_0018' END), A.DTL_PRCS_STTS_CD, #{langCd}) AS DTL_PRCS_STTS_NM /** Code d'état détaillé du traitement !== 상세처리상태코드 ==! */ , A.WRIOP_RSLT_CD /** Code du résultat d'avis !== 의견서결과코드 ==! */ , FN_GET_CD_VDVAL_NM('AEO_0009', A.WRIOP_RSLT_CD, #{langCd}) AS WRIOP_RSLT_NM /** Code du résultat d'avis !== 의견서결과코드 ==! */ , A.WRIOP_CN /** Contenu d'avis !== 의견서내용 ==! */ , A.ATCH_FILE_ID /** Id Du Fichier Joint !== 첨부파일ID ==! */ , TO_CHAR(A.SBMT_DTTM, 'DD/MM/YYYY hh24:mi:ss') AS SBMT_DTTM /** Date Et Heure De Soumission !== 제출일시 ==! */ , A.DEL_YN /** Suppression On !== 삭제여부 ==! */ , A.FRST_REGST_ID /** Id Du Premier Enregistrant !== 최초등록자ID ==! */ , (SELECT EMP_NM FROM TB_POTI_EMP WHERE PBSR_NO = A.FRST_REGST_ID) AS FRST_REGST_NM , TO_CHAR(A.FRST_RGSR_DTTM, 'DD/MM/YYYY') AS frstRgsrDttm /** Date Et Heure De Premier Enregistrement !== 최초등록일시 ==! */ , A.LAST_CHPR_ID /** Id Du Modificateur Final !== 최종변경자ID ==! */ , (SELECT EMP_NM FROM TB_POTI_EMP WHERE PBSR_NO = A.LAST_CHPR_ID) AS LAST_CHPR_NM , TO_CHAR(A.LAST_CHG_DTTM, 'DD/MM/YYYY hh24:mi:ss') AS lastChgDttm /** Date Et Heure De Modification Finale !== 최종변경일시 ==! */ FROM TB_AEOI_WRIOP A /** AEOI_AVIS !== AEOI_의견서 ==! */ WHERE A.DEL_YN = 'N' /** Suppression ON !== 삭제여부 ==! */
AND A.AEO_RQST_NO IN /** Numéro de demande du statut OEA !== AEO신청번호 ==! */
#{item}
OR (A.AEO_ATHR_NO = #{aeoAthrNo} AND A.DEL_YN = 'N')
AND ( A.SBMT_DTTM IS NULL AND A.LAST_CHPR_ID = #{lastChprId} /** 로그인 ID */ OR A.SBMT_DTTM IS NOT NULL ) ORDER BY A.LAST_CHG_DTTM DESC
