mapper.select:
/* CarCagDcshBl_selectCarCagDcshBl*/

      AND A.mrn = #{mrn}                            /** !== MRN ==! */
      AND A.cag_mgmt_no = #{cagMgmtNo}              /**  !== 화물관리번호 ==! */

/* CarCagDcshBl_selectCarCagDcshBlCnt */
SELECT count(*) cnt
  FROM tb_cari_cag_dcsh_bl
 WHERE mrn = #{mrn}
   AND bl_pt_cd = #{blPtCd}
   AND bl_no = #{blNo}

   AND hbl_no = #{hblNo}

   AND del_yn = 'N'

mapper.update:
/* CarCagDcshBlMapper.updateCagDcshBlEptIbobzCd */
UPDATE tb_care_cag_Dcsh_bl
SET LAST_CHPR_ID = #{lastChprId}
  , LAST_CHG_DTTM = SYSTIMESTAMP

  , IBOBZ_CD = #{ibobzCd}

WHERE cag_mgmt_no = #{cagMgmtNo}
  AND cag_dcsh_rgsr_mgmt_no = #{cagDcshRgsrMgmtNo}

/* CarCagDcshBlMapper.updateCagDcshBlIptIbobzCd */
UPDATE tb_cari_cag_Dcsh_bl
SET LAST_CHPR_ID = #{lastChprId}
  , LAST_CHG_DTTM = SYSTIMESTAMP

  , IBOBZ_CD = #{ibobzCd}

WHERE cag_mgmt_no = #{cagMgmtNo}
  AND mrn = #{mrn}
