mapper.select:
/* CarCagLdunRprt.selectCariLdunRprtCntrTotCnt */
SELECT COUNT(*) AS TOT_CNT
  FROM (


       ) SQLRSLT

/* CarCagLdunRprt.selectCariLdunRprtCntr */
SELECT #{totCnt} AS TOT_CNT , SQLRSLT.* , ROWNUM as RNUM FROM (
ORDER BY CASE WHEN(T1.apre_yn_cd = 'O') THEN 3 WHEN(T1.apre_yn_cd = 'T') THEN 2 WHEN(T1.apre_yn_cd = 'N') THEN 1 ELSE 0 END, T1.bl_no, T1.cntr_no

mapper.update:
/* CarCagLdunRprtCntrMapper.apreYnCdCarLdunRprtCntr */ UPDATE TB_CARI_LDUN_RPRT_CNTR SET del_yn = 'N' /** Suppression ON !== 삭제여부 ==! */ , last_chpr_id = #{lastChprId} /** ID du modificateur final !== 최종변경자ID ==! */ , last_chg_dttm = SYSTIMESTAMP /** Date et heure de modification finale !== 최종변경일시 ==! */ , divd_ldun_last_yn = #{divdLdunLastYn} /** Dernier Déchargement Partiel On !== 분할하역최종여부 ==! */ , insc_cfrm_dt = TO_CHAR(TO_DATE( #{inscCfrmDt}, 'DD/MM/YYYY'),'DDMMYYYY') /** Date de vérification de la visite !== 검사확인일자 ==! */ , apre_yn_cd = #{apreYnCd} /** Autorisation O/N !== 승인여부코드 ==! */ , rtrn_rsn_cn = #{rtrnRsnCn} /** Motif De Rejet !== 반려사유내용 ==! */ WHERE 1=1 AND ldun_rprt_no = #{ldunRprtNo}
AND bl_no = #{blNo} /** N° De Bl !== BL번호 ==! */ AND cntr_srno = #{cntrSrno} /** N° De Séquence Du Conteneur !== 컨테이너일련번호 ==! */
AND dscr_tp_cd = 'OK' /** Déficit/Excédent != 불일치구분코드 =! */
AND dscr_tp_cd != 'OK' /** Déficit/Excédent != 불일치구분코드 =! */

mapper.select:
/* CarCagLdunRprtCntrMapper.selectCarLdunRprtCntrByApre */ SELECT ldun_rprt_no , bl_no , cntr_no , cntr_srno FROM tb_cari_ldun_rprt_cntr WHERE 1=1 AND del_yn = 'N' AND ldun_rprt_no = #{ldunRprtNo}
AND apre_yn_cd = #{apreYnCd} /** Autorisation O/N !== 승인여부코드 ==! */
AND apre_yn_cd IS NULL
AND ROWNUM = 1
