/* ClrBfhnJdgnAdorHnotMgmtMapper.selectPdlsClsfBfhnJdgnHnotAggtPcdnList */
select '20' otrq_tp_cd , st.cstm_cd , fn_get_orgn_nm(st.cstm_cd) as cstm_nm , st.dept_cd , fn_get_orgn_nm(st.dept_cd) as dept_nm , st.audt_emp_id , (select emp_nm from tb_poti_emp where pbsr_no = st.audt_emp_id) as audt_emp_nm , sum(st.hnot_gcnt) as hnot_gcnt , sum(st.audt_prgs_cnt) as audt_prgs_cnt , sum(st.audt_cmpl_cnt) as audt_cmpl_cnt , sum(st.audt_dsms_cnt) as audt_dsms_cnt , sum(st.audt_cncl_cnt) as audt_cncl_cnt from ( select t2.cstm_cd , t2.dept_cd , t2.audt_emp_id , case when t2.prcs_stts_cd is not null then 1 else 0 end as hnot_gcnt /**배부총건수*/ , case when t2.prcs_stts_cd in ('B02','D02','D05','F01','I01','I02','I03','I05','I06') then 1 else 0 end as audt_prgs_cnt /**심사중*/ , case when t2.prcs_stts_cd in ('D08','J04') then 1 else 0 end as audt_cmpl_cnt /**심사완료*/ , case when t2.prcs_stts_cd = 'D09' then 1 else 0 end as audt_dsms_cnt /**심사기각*/ , case when t2.prcs_stts_cd = 'G03' then 1 else 0 end as audt_cncl_cnt /**심사취소*/ from TB_CLRE_RTC_RQST t1 , TB_CLRI_RTC_AUDT t2 where t1.del_yn = 'N' and t1.last_yn = 'Y' and t1.pdls_clsf_bfhn_jdgn_rqst_no = t2.pdls_clsf_bfhn_jdgn_rqst_no and t2.del_yn = 'N' and t2.last_yn = 'Y' and nvl(t2.audt_emp_id,'') != ''
and t1.rqst_dt between TO_CHAR(TO_DATE(#{rqstDtFrom}, 'DD/MM/YYYY'), 'YYYYMMDD') and TO_CHAR(TO_DATE(#{rqstDtTo}, 'DD/MM/YYYY'), 'YYYYMMDD')
and t2.cstm_cd = #{cstmCd}
and t2.dept_cd = #{deptCd}
) st group by st.cstm_cd , st.dept_cd , st.audt_emp_id order by st.cstm_cd , st.dept_cd , st.audt_emp_id

