mapper.select:
/* selectPrcsSttsCd */
SELECT PRCS_STTS_CD
FROM TB_CLRI_DED_COMN
WHERE DTL_DCSH_NO = #{dtlDcshNo}
  AND DEL_YN = 'N'

/* selectAudtStrtYn */
SELECT 1 AS AUDT_STRT_YN
FROM TB_CLRI_DED_VLUT_AUDT_COMN
WHERE DTL_DCSH_NO = #{dtlDcshNo}
  AND LAST_YN = 'Y'
  AND VLUT_PRCS_STTS_CD IN ('R','T') /* R : 가격평가 등록상태, T : 가격평가 통보상태 (심사결과 )*/

mapper.update:
/* updatePrcsSttsCd */
UPDATE TB_CLRI_DED_COMN
SET PRCS_STTS_CD = #{prcsSttsCd}
  , LAST_CHPR_ID = #{lastChprId}
  , LAST_CHG_DTTM =  SYSTIMESTAMP
WHERE DTL_DCSH_NO = #{dtlDcshNo}
  AND DEL_YN = 'N'

/* updatePrcsSttsCd */
UPDATE TB_CLRI_DED_COMN
SET PRCS_STTS_CD = #{prcsSttsCd}
  , LAST_CHPR_ID = #{lastChprId}
  , LAST_CHG_DTTM =  SYSTIMESTAMP
WHERE DTL_DCSH_NO = #{dtlDcshNo}
  AND DEL_YN = 'N'

mapper.select:
/* ClriComPrcs_selectPrcsBrkd */
SELECT *
FROM
(
SELECT PRCS_STTS_CD
, TO_CHAR(PRCS_DTRM_DTTM,'DD/MM/YYYY HH24:MI:SS') AS PRCS_DTRM_DTTM
FROM TB_CLRI_DED_PRCS_BRKD
WHERE DEL_YN = 'N'
AND   DTL_DCSH_NO = #{dtlDcshNo}

	AND PRCS_STTS_CD = #{prcsSttsCd}


	AND PRCS_STTS_PR_PRFR_CD = #{prcsSttsPrPrfrCd}

ORDER BY PRCS_SRNO DESC
)
WHERE ROWNUM = 1

/* ClriComPrcs_selectPrcsSttsHistYn */
SELECT 1
FROM TB_CLRI_DED_PRCS_BRKD
WHERE DTL_DCSH_NO = #{dtlDcshNo}
AND DEL_YN = 'N'
AND PRCS_STTS_CD = #{prcsSttsCd}

	AND PRCS_STTS_PR_PRFR_CD = #{prcsSttsPrPrfrCd}

AND ROWNUM = 1
ORDER BY PRCS_SRNO DESC

/* ClriAfdDcsh_selectPrcsBrkdList */ SELECT COUNT(*) OVER() AS totCnt, SQLRSLT.*, rownum as rnum FROM ( SELECT A.PRCS_SRNO , TO_CHAR(A.PRCS_DTRM_DTTM,'DD/MM/YYYY HH24:MI:SS') AS PRCS_DTRM_DTTM , A.PRCS_STTS_CD , (SELECT FN_GET_CD_VDVAL_NM('CLR_0011', A.PRCS_STTS_CD, #{langCd}) FROM DUAL) AS PRCS_STTS_NM , DECODE( A.PRCS_STTS_CD, 'A04', 'ST' , 'B01', 'ST' , 'C02', 'ST' , 'B02', 'ST' , 'D01', 'PB' , 'F01', 'PB' , 'D02', 'PB' , 'D03', 'PB' , 'D04', 'ST', 'DC') AS ACT_TP , A.PRCS_STTS_PR_PRFR_CD , (SELECT FN_GET_CD_VDVAL_NM('CLR_0012', A.PRCS_STTS_PR_PRFR_CD, #{langCd}) FROM DUAL) AS PRCS_STTS_PR_PRFR_NM , CASE WHEN REGEXP_SUBSTR(A.PRCS_STTS_CN, '\[msg.[0-9]{5}\]', 1, 1) IS NOT NULL THEN ( SELECT MSG_NM FROM TB_COM_MSG_LANG WHERE MSG_ID = SUBSTR(A.PRCS_STTS_CN, 2, 9) and LANG_CD = #{langCd} and DEL_YN = 'N' ) ELSE A.PRCS_STTS_CN END AS PRCS_STTS_CN , A.LAST_CHPR_ID , NVL((SELECT USER_NM FROM ( SELECT USER_NM FROM TB_COM_CO_USER WHERE USER_ID = A.LAST_CHPR_ID UNION ALL SELECT C.EMP_NM FROM TB_POTI_EMP C, TB_POTI_ORGN D WHERE C.ORGN_MGMT_CD = D.ORGN_MGMT_CD AND C.DEL_YN = 'N' AND D.DEL_YN = 'N' AND C.PBSR_NO = A.LAST_CHPR_ID ) WHERE ROWNUM = 1 ) , 'SYSTEM') AS LAST_CHPR_NM
FROM TB_CLRI_DED_PRCS_BRKD A WHERE A.DEL_YN = 'N'
AND DTL_DCSH_NO = #{dtlDcshNo}
ORDER BY PRCS_SRNO DESC ) SQLRSLT

mapper.update:
/* ClriComPrcs_updateDputYn */
UPDATE TB_CLRI_DED_COMN
SET DPUT_YN = #{dputYn}
  , LAST_CHPR_ID = #{lastChprId}
  , LAST_CHG_DTTM =  SYSTIMESTAMP
WHERE DTL_DCSH_NO = #{dtlDcshNo}
  AND DEL_YN = 'N'

/* ClriComPrcs_updateCnclDclrRcpnYn */
UPDATE TB_CLRI_DED_COMN
SET CNCL_DCLR_RCPN_YN = #{cnclDclrRcpnYn}
  , LAST_CHPR_ID = #{lastChprId}
  , LAST_CHG_DTTM =  SYSTIMESTAMP
WHERE DTL_DCSH_NO = #{dtlDcshNo}
  AND DEL_YN = 'N'

mapper.select:
/* ClriComPrcs_selectCagTrcnDclrPtYn */
SELECT 1 AS CAG_TRCN_DCLR_PT_YN
FROM TB_CLRI_DED_COMN
WHERE DTL_DCSH_NO = #{dtlDcshNo}
  AND DCLR_PT_CD IN ('IM4', 'IM5', 'IM6', 'IM7', 'IM9')
  AND DEL_YN = 'N'

/* ClriComPrcs_selectBfAsedPymnTax */
SELECT ASED_PYMN_TAX_TAMT FROM (
		   SELECT ASED_TAX_TAMT + ASED_OTHS_PCFE_AMT AS ASED_PYMN_TAX_TAMT
		   FROM TB_CLRI_DED_VLUT_AUDT_COMN
		   WHERE ( NTFC_NO IS NOT NULL OR VLUT_PRCS_STTS_CD = 'D08' )
			 AND DTL_DCSH_NO = #{dtlDcshNo}
			 AND PRC_EV_DGCNT  <  #{prcEvDgcnt}
		   ORDER BY PRC_EV_DGCNT DESC
							   )
WHERE ROWNUM = 1
