mapper.select:
/* ClrRegmRqstMdlComn_selectClrRegmRqstMdlComn */ SELECT A.regm_rqst_cd /** N° de demande de régime !== 레짐신청번호 ==! */ , A.mdfy_dgcnt /** Fréquence de rectification !== 정정차수 ==! */ , (select regm_mdl_cd from tb_clri_epc_apc_cd_mpng_brkd where epc_cd = a.epc_cd and apc_cd = a.apc_cd) AS REGM_MDL_CD , FN_GET_CD_VDVAL_NM('CLR_0131', (select regm_mdl_cd from tb_clri_epc_apc_cd_mpng_brkd where epc_cd = a.epc_cd and apc_cd = a.apc_cd), #{langCd}) as REGM_MDL_CD_NM , A.rqst_sbmt_cstm_cd /** Bureau de dépôt de la demande !== 신청제출세관코드 ==! */ , A.rqst_sbmt_dept_cd /** Bureau de dépôt de la demande !== 신청제출부서코드 ==! */ , A.epc_cd /** Code EPC !== EPC코드 ==! */ , A.apc_cd /** Code Apc !== APC코드 ==! */ , A.aplc_tp_cd /** Qualité du demandeur !== 신청인구분코드 ==! */ , A.csbr_nm /** Nom Du Cda !== 관세사명 ==! */ , A.csbr_aplc_id /** Demandeur !== 관세사신청인ID ==! */ , A.csbr_perm_no /** N° Agrément du commissionnaire en douane !== 관세사허가번호 ==! */ , A.csbr_perm_stts_cd /** Statut de l'agrément !== 관세사허가상태코드 ==! */ , A.csbr_mnda_info /** Reference mandat !== 관세사위임정보 ==! */ , A.csbr_mnda_stts_cd /** Statut mandat !== 관세사위임상태코드 ==! */ , A.co_aplc_id /** Demandeur !== 업체신청인ID ==! */ , A.co_nm /** Nom De L'Entreprise !== 업체명 ==! */ , A.co_aplc_lgl_posi_cd /** Statut juridique du demandeur !== 업체신청인법적지위코드 ==! */ , A.potx_idfy_no /** Numéro d'identification fiscale !== 납세식별번호 ==! */ , A.nif_stts_cd /** Code De Statut NIF !== 납세식별번호상태코드 ==! */ , A.cmrc_regs_no /** Numéro de registre du commerce !== 상업등기번호 ==! */ , A.cmrc_regs_stts_cd /** État de registre du commerce !== 상업등기상태코드 ==! */ , A.co_addr /** Adresse De L'Entreprise !== 업체주소 ==! */ , A.co_ovrs_addr /** Adresse à l'étranger !== 업체해외주소 ==! */ , A.rcvr_fax_no /** Destinataire Fax Numero !== 수신자팩스번호 ==! */ , A.rcvr_mobl_no /** Destinataire Telephone Mobile Numero !== 수신자휴대전화번호 ==! */ , A.rcvr_eml /** E-Mail Du Receveur !== 수신자이메일 ==! */ , A.prcs_stts_cd /** Code De Statut De Traitement !== 처리상태코드 ==! */ , FN_GET_CD_VDVAL_NM('CLR_0103', A.PRCS_STTS_CD, #{langCd}) AS PRCS_STTS_NM , A.atch_file_id /** Id Du Fichier Joint !== 첨부파일ID ==! */ , A.last_yn /** Final On !== 최종여부 ==! */ , A.del_yn /** Suppression On !== 삭제여부 ==! */ , A.frst_regst_id /** Id Du Premier Enregistrant !== 최초등록자ID ==! */ , A.frst_rgsr_dttm /** Date Et Heure De Premier Enregistrement !== 최초등록일시 ==! */ , A.last_chpr_id /** Id Du Modificateur Final !== 최종변경자ID ==! */ , A.last_chg_dttm /** Date Et Heure De Modification Finale !== 최종변경일시 ==! */ , TO_CHAR(TO_DATE(A.rqst_dt, 'YYYYMMDD'), 'DD/MM/YYYY') AS rqst_dt /** Date de demande !== 신청일자 ==! */ , frst_regm_pn /** N° de l'autorisation de régime initial !== 최초레짐허가번호 ==! */ , bf_regm_pn /** N° de l'autorisation de régime précédent !== 이전레짐허가번호 ==! */ FROM TB_CLRE_REGM_RQST_MDL_COMN A WHERE A.regm_rqst_cd = #{regmRqstCd} /** N° de demande de régime !== 레짐신청번호 ==! */
AND A.mdfy_dgcnt = #{mdfyDgcnt} /** Fréquence de rectification !== 정정차수 ==! */
AND A.last_yn = #{lastYn}
AND A.last_yn = 'Y'
AND A.last_yn = #{lastYn}

/* ClrRegmRqstMdlComn_selectClrRegmRqstMdlComnList */
SELECT A.regm_rqst_cd /** N° de demande de régime !== 레짐신청번호 ==! */ , A.mdfy_dgcnt /** Fréquence de rectification !== 정정차수 ==! */ , (select regm_mdl_cd from tb_clri_epc_apc_cd_mpng_brkd where epc_cd = a.epc_cd and apc_cd = a.apc_cd) AS REGM_MDL_CD , FN_GET_CD_VDVAL_NM('CLR_0131', (select regm_mdl_cd from tb_clri_epc_apc_cd_mpng_brkd where epc_cd = a.epc_cd and apc_cd = a.apc_cd), #{langCd}) as REGM_MDL_CD_NM , A.rqst_sbmt_cstm_cd /** Bureau de dépôt de la demande !== 신청제출세관코드 ==! */ , FN_GET_ORGN_NM(A.rqst_sbmt_cstm_cd) as rqst_sbmt_cstm_nm , A.rqst_sbmt_dept_cd /** Bureau de dépôt de la demande !== 신청제출부서코드 ==! */ , FN_GET_ORGN_NM(A.rqst_sbmt_dept_cd) as rqst_sbmt_dept_nm , A.epc_cd /** Code EPC !== EPC코드 ==! */ , A.apc_cd /** Code Apc !== APC코드 ==! */ , A.aplc_tp_cd /** Qualité du demandeur !== 신청인구분코드 ==! */ , A.csbr_nm /** Nom Du Cda !== 관세사명 ==! */ , A.csbr_aplc_id /** Demandeur !== 관세사신청인ID ==! */ , A.csbr_perm_no /** N° Agrément du commissionnaire en douane !== 관세사허가번호 ==! */ , A.csbr_perm_stts_cd /** Statut de l'agrément !== 관세사허가상태코드 ==! */ , A.csbr_mnda_info /** Reference mandat !== 관세사위임정보 ==! */ , A.csbr_mnda_stts_cd /** Statut mandat !== 관세사위임상태코드 ==! */ , A.co_aplc_id /** Demandeur !== 업체신청인ID ==! */ , A.co_nm /** Nom De L'Entreprise !== 업체명 ==! */ , A.co_aplc_lgl_posi_cd /** Statut juridique du demandeur !== 업체신청인법적지위코드 ==! */ , A.potx_idfy_no /** Numéro d'identification fiscale !== 납세식별번호 ==! */ , A.nif_stts_cd /** Code De Statut NIF !== 납세식별번호상태코드 ==! */ , A.cmrc_regs_no /** Numéro de registre du commerce !== 상업등기번호 ==! */ , A.cmrc_regs_stts_cd /** État de registre du commerce !== 상업등기상태코드 ==! */ , A.co_addr /** Adresse De L'Entreprise !== 업체주소 ==! */ , A.co_ovrs_addr /** Adresse à l'étranger !== 업체해외주소 ==! */ , A.rcvr_fax_no /** Destinataire Fax Numero !== 수신자팩스번호 ==! */ , A.rcvr_mobl_no /** Destinataire Telephone Mobile Numero !== 수신자휴대전화번호 ==! */ , A.rcvr_eml /** E-Mail Du Receveur !== 수신자이메일 ==! */ , A.prcs_stts_cd /** Code De Statut De Traitement !== 처리상태코드 ==! */ , FN_GET_CD_VDVAL_NM('CLR_0103', A.PRCS_STTS_CD, #{langCd}) AS PRCS_STTS_NM , A.atch_file_id /** Id Du Fichier Joint !== 첨부파일ID ==! */ , A.last_yn /** Final On !== 최종여부 ==! */ , A.del_yn /** Suppression On !== 삭제여부 ==! */ , A.frst_regst_id /** Id Du Premier Enregistrant !== 최초등록자ID ==! */ , A.frst_rgsr_dttm /** Date Et Heure De Premier Enregistrement !== 최초등록일시 ==! */ , A.last_chpr_id /** Id Du Modificateur Final !== 최종변경자ID ==! */ , A.last_chg_dttm /** Date Et Heure De Modification Finale !== 최종변경일시 ==! */ , (select audt_emp_id from tb_clri_regm_audt_mdl_comn where A.regm_rqst_cd = regm_rqst_cd and last_Yn = 'Y' and A.mdfy_dgcnt = mdfy_dgcnt) as audt_emp_id , TO_CHAR(TO_DATE(A.rqst_dt, 'YYYYMMDD'), 'DD/MM/YYYY') AS rqst_dt /** Date de demande !== 신청일자 ==! */ , A.frst_regm_pn /** N° de l'autorisation de régime initial !== 최초레짐허가번호 ==! */ , A.bf_regm_pn /** N° de l'autorisation de régime précédent !== 이전레짐허가번호 ==! */ , (select regm_pn from tb_clri_regm_audt_mdl_comn where regm_rqst_cd = a.regm_rqst_cd and mdfy_dgcnt = a.mdfy_dgcnt) as regm_pn FROM TB_CLRE_REGM_RQST_MDL_COMN A WHERE A.del_yn = 'N' /** Suppression ON !== 삭제여부 ==! */ AND A.last_yn = 'Y'
AND A.regm_rqst_cd in (select regm_rqst_cd from tb_clri_regm_audt_mdl_comn where audt_emp_id = #{clrRegmAudtMdlComnVo.audtEmpId}) /** Registre de gestion de garantie du système ALCES(Dédouanement 508, Dédouanement 510)*/
AND A.prcs_stts_cd in
#{prcsSttsCd}
AND A.prcs_stts_cd = #{prcsSttsCd}
AND A.prcs_stts_cd IN ('A04', 'A06', 'J02', 'D01', 'B02', 'D08', 'D09','C01')
AND A.prcs_stts_cd IN ('B02', 'D02', 'D05', 'F01', 'I01', 'D08', 'D09', 'J05')
AND A.regm_rqst_cd = #{regmRqstCd} /** N° de demande de régime !== 레짐신청번호 ==! */
/** 신고섹터 */ AND A.rqst_sbmt_cstm_cd IN (SELECT ORGN_CD FROM TB_POTI_ORGN WHERE DEL_YN ='N' AND UPR_ORGN_MGMT_CD = (SELECT ORGN_MGMT_CD FROM TB_POTI_ORGN WHERE ORGN_CD = #{dclrSctrCd} AND DEL_YN ='N'))
/** 신고세관 */ AND A.rqst_sbmt_cstm_cd = #{dclrCstmCd}
/** 부서 */ AND A.rqst_sbmt_dept_cd = #{deptCd}
AND A.rqst_sbmt_cstm_cd = #{rqstSbmtCstmCd}
AND A.rqst_sbmt_dept_cd = #{rqstSbmtDeptCd}
AND A.potx_idfy_no = #{potxIdfyNo}
AND A.epc_cd = #{epcCd}
AND A.apc_cd = #{apcCd}
AND A.rqst_dt BETWEEN TO_CHAR(TO_DATE(#{rqstDtFrom}, 'DD/MM/YYYY'), 'YYYYMMDD') AND TO_CHAR(TO_DATE(#{rqstDtTo}, 'DD/MM/YYYY'), 'YYYYMMDD')
ORDER BY frst_rgsr_dttm desc

/* ClrRegmRqstMdlComn_selectClrRegmRqstMdlComnInputList */
