/* ClrRegmRqstMdlComn_selectClrRegmRqstHistList */
SELECT A.regm_rqst_cd /** N° de demande de régime !== 레짐신청번호 ==! */ , A.mdfy_dgcnt /** Fréquence de rectification !== 정정차수 ==! */ , (select regm_mdl_cd from tb_clri_epc_apc_cd_mpng_brkd where epc_cd = a.epc_cd and apc_cd = a.apc_cd) AS REGM_MDL_CD , FN_GET_CD_VDVAL_NM('CLR_0131', (select regm_mdl_cd from tb_clri_epc_apc_cd_mpng_brkd where epc_cd = a.epc_cd and apc_cd = a.apc_cd), #{langCd}) as REGM_MDL_CD_NM , A.rqst_sbmt_cstm_cd /** Bureau de dépôt de la demande !== 신청제출세관코드 ==! */ , FN_GET_ORGN_NM(A.rqst_sbmt_cstm_cd) as rqst_sbmt_cstm_nm , A.rqst_sbmt_dept_cd /** Bureau de dépôt de la demande !== 신청제출부서코드 ==! */ , FN_GET_ORGN_NM(A.rqst_sbmt_dept_cd) as rqst_sbmt_dept_nm , A.epc_cd /** Code EPC !== EPC코드 ==! */ , A.apc_cd /** Code Apc !== APC코드 ==! */ , A.aplc_tp_cd /** Qualité du demandeur !== 신청인구분코드 ==! */ , A.csbr_nm /** Nom Du Cda !== 관세사명 ==! */ , A.csbr_aplc_id /** Demandeur !== 관세사신청인ID ==! */ , A.csbr_perm_no /** N° Agrément du commissionnaire en douane !== 관세사허가번호 ==! */ , A.csbr_perm_stts_cd /** Statut de l'agrément !== 관세사허가상태코드 ==! */ , A.csbr_mnda_info /** Reference mandat !== 관세사위임정보 ==! */ , A.csbr_mnda_stts_cd /** Statut mandat !== 관세사위임상태코드 ==! */ , A.co_aplc_id /** Demandeur !== 업체신청인ID ==! */ , A.co_nm /** Nom De L'Entreprise !== 업체명 ==! */ , A.co_aplc_lgl_posi_cd /** Statut juridique du demandeur !== 업체신청인법적지위코드 ==! */ , A.potx_idfy_no /** Numéro d'identification fiscale !== 납세식별번호 ==! */ , A.nif_stts_cd /** Code De Statut NIF !== 납세식별번호상태코드 ==! */ , A.cmrc_regs_no /** Numéro de registre du commerce !== 상업등기번호 ==! */ , A.cmrc_regs_stts_cd /** État de registre du commerce !== 상업등기상태코드 ==! */ , A.co_addr /** Adresse De L'Entreprise !== 업체주소 ==! */ , A.co_ovrs_addr /** Adresse à l'étranger !== 업체해외주소 ==! */ , A.rcvr_fax_no /** Destinataire Fax Numero !== 수신자팩스번호 ==! */ , A.rcvr_mobl_no /** Destinataire Telephone Mobile Numero !== 수신자휴대전화번호 ==! */ , A.rcvr_eml /** E-Mail Du Receveur !== 수신자이메일 ==! */ , A.prcs_stts_cd /** Code De Statut De Traitement !== 처리상태코드 ==! */ , FN_GET_CD_VDVAL_NM('CLR_0103', A.PRCS_STTS_CD, #{langCd}) AS PRCS_STTS_NM , A.atch_file_id /** Id Du Fichier Joint !== 첨부파일ID ==! */ , A.last_yn /** Final On !== 최종여부 ==! */ , A.del_yn /** Suppression On !== 삭제여부 ==! */ , A.frst_regst_id /** Id Du Premier Enregistrant !== 최초등록자ID ==! */ , A.frst_rgsr_dttm /** Date Et Heure De Premier Enregistrement !== 최초등록일시 ==! */ , A.last_chpr_id /** Id Du Modificateur Final !== 최종변경자ID ==! */ , A.last_chg_dttm /** Date Et Heure De Modification Finale !== 최종변경일시 ==! */ , TO_CHAR(TO_DATE(A.rqst_dt, 'YYYYMMDD'), 'DD/MM/YYYY') AS rqst_dt /** Date De Demande !== 신청일자 ==! */ , (select TO_CHAR(TO_DATE(perm_tmlm_strt_dt, 'YYYYMMDD'), 'DD/MM/YYYY') from tb_clri_regm_audt_mdl_comn where regm_rqst_cd = A.regm_rqst_cd and mdfy_dgcnt = A.mdfy_dgcnt) AS perm_tmlm_strt_dt /** Date de début de délai accordé !== 허가기한시작일자 ==! */ , (select TO_CHAR(TO_DATE(perm_tmlm_end_dt, 'YYYYMMDD'), 'DD/MM/YYYY') from tb_clri_regm_audt_mdl_comn where regm_rqst_cd = A.regm_rqst_cd and mdfy_dgcnt = A.mdfy_dgcnt) AS perm_tmlm_end_dt /** Date de fin de délai accordé !== 허가기한종료일자 ==! */ , (select audt_emp_id from tb_clri_regm_audt_mdl_comn where A.regm_rqst_cd = regm_rqst_cd and last_Yn = 'Y') as audt_emp_id , A.frst_regm_pn /** N° de l'autorisation de régime initial !== 최초레짐허가번호 ==! */ , A.bf_regm_pn /** N° de l'autorisation de régime précédent !== 이전레짐허가번호 ==! */ FROM TB_CLRE_REGM_RQST_MDL_COMN A WHERE 1 = 1
AND A.regm_rqst_cd IN #{item}
AND A.regm_rqst_cd = #{regmRqstCd} /** N° de demande de régime !== 레짐신청번호 ==! */
ORDER BY frst_rgsr_dttm desc

