/* insertClrRegmRqstMdl7 */
INSERT INTO TB_CLRE_REGM_RQST_MDL_7 (
          regm_rqst_cd
        , mdfy_dgcnt
        , rqst_rsn
        , imp_dclr_pt_cd
        , imp_dcsh_no
        , imp_dcsh_dt
        , rqst_stts_cd
        , temp_imex_cstm_cd
        , mg_need_yn
        , mg_strt_dt
        , mg_end_dt
        , reqst_strt_dt
        , reqst_end_dt
        , del_yn
        , frst_regst_id
        , frst_rgsr_dttm
        , last_chpr_id
        , last_chg_dttm
) VALUES (
         #{regmRqstCd}
         , #{mdfyDgcnt}
         , #{rqstRsn}
         , #{impDclrPtCd}
         , #{impDcshNo}
         , TO_CHAR(TO_DATE(#{impDcshDt}, 'DD/MM/YYYY'),'YYYYMMDD')
         , #{rqstSttsCd}
         , #{tempImexCstmCd}
         , #{mgNeedYn}
         , TO_CHAR(TO_DATE(#{mgStrtDt}, 'DD/MM/YYYY'),'YYYYMMDD')
         , TO_CHAR(TO_DATE(#{mgEndDt}, 'DD/MM/YYYY'),'YYYYMMDD')
         , TO_CHAR(TO_DATE(#{reqstStrtDt}, 'DD/MM/YYYY'),'YYYYMMDD')
         , TO_CHAR(TO_DATE(#{reqstEndDt}, 'DD/MM/YYYY'),'YYYYMMDD')
         , 'N'
         , #{frstRegstId}
         , SYSTIMESTAMP
         , #{lastChprId}
         , SYSTIMESTAMP
         )
    ON CONFLICT ( regm_rqst_cd,mdfy_dgcnt )
DO UPDATE SET
    rqst_rsn = #{rqstRsn}
   ,imp_dclr_pt_cd = #{impDclrPtCd}
   ,imp_dcsh_no = #{impDcshNo}
   ,imp_dcsh_dt = TO_CHAR(TO_DATE(#{impDcshDt}, 'DD/MM/YYYY'),'YYYYMMDD')
   ,rqst_stts_cd = #{rqstSttsCd}
   ,temp_imex_cstm_cd = #{tempImexCstmCd}
   ,mg_need_yn = #{mgNeedYn}
   ,mg_strt_dt = TO_CHAR(TO_DATE(#{mgStrtDt}, 'DD/MM/YYYY'),'YYYYMMDD')
