mapper.select:
/** selectWlyCdList */
SELECT A.WLY_CD , B.WLY_NM , (SELECT WLY_NM FROM TB_COM_WLY_CD_LANG WHERE DEL_YN = 'N' AND WLY_CD = A.WLY_CD AND LANG_CD = 'FR') AS WLY_NM_FR , (SELECT WLY_NM FROM TB_COM_WLY_CD_LANG WHERE DEL_YN = 'N' AND WLY_CD = A.WLY_CD AND LANG_CD = 'EN') AS WLY_NM_EN , (SELECT WLY_NM FROM TB_COM_WLY_CD_LANG WHERE DEL_YN = 'N' AND WLY_CD = A.WLY_CD AND LANG_CD = 'KO') AS WLY_NM_KO , (SELECT WLY_NM FROM TB_COM_WLY_CD_LANG WHERE DEL_YN = 'N' AND WLY_CD = A.WLY_CD AND LANG_CD = 'AR') AS WLY_NM_AR FROM TB_COM_WLY_CD A, TB_COM_WLY_CD_LANG B WHERE A.DEL_YN = 'N' AND B.DEL_YN = 'N' AND A.WLY_CD = B.WLY_CD AND B.LANG_CD = #{langCd}
AND A.WLY_CD = #{searWlyCd}
AND UPPER(B.WLY_NM) LIKE UPPER('%'||#{searWlyNm}||'%')
ORDER BY A.WLY_CD

/** selectWlyCdDup */
SELECT
   COUNT(1)
FROM  TB_COM_WLY_CD A
WHERE A.DEL_YN = 'N'
AND   A.WLY_CD = #{wlyCd}

mapper.insert:
/** insertWlyCdLang */
with upsert as
(
    UPDATE TB_COM_WLY_CD_LANG
    SET
           LAST_CHPR_ID = #{lastChprId}
         , LAST_CHG_DTTM = SYSTIMESTAMP
         , DEL_YN = 'N'
         , WLY_NM = #{wlyNm}
    WHERE WLY_CD = #{wlyCd}
    AND   LANG_CD = #{langCd}
    returning *
)
INSERT INTO TB_COM_WLY_CD_LANG
(
      WlY_CD
    , LANG_CD
    , WLY_NM
    , DEL_YN
    , FRST_REGST_ID
    , FRST_RGSR_DTTM
    , LAST_CHPR_ID
    , LAST_CHG_DTTM
)
SELECT
       #{wlyCd}
     , #{langCd}
     , #{wlyNm}
     , 'N'
     , #{frstRegstId}
     , SYSTIMESTAMP
     , #{lastChprId}
     , SYSTIMESTAMP
WHERE NOT exists(select * from upsert)

mapper.update:
/** deleteWlyCd */
UPDATE TB_COM_WLY_CD
SET
    LAST_CHPR_ID = #{lastChprId}
  , LAST_CHG_DTTM = SYSTIMESTAMP
  , DEL_YN = 'Y'
WHERE Wly_CD = #{wlyCd}

/** deleteWlyCdLang */
    UPDATE TB_COM_WLY_CD_LANG
    SET
        LAST_CHPR_ID = #{lastChprId}
      , LAST_CHG_DTTM = SYSTIMESTAMP
      , DEL_YN = 'Y'
    WHERE WLY_CD = #{wlyCd}

  /** deleteCommCdWly */
