mapper.select:
/** selectWlyPostCdDup */
SELECT
    COUNT(1)
FROM  TB_COM_POST_CD A
WHERE A.DEL_YN = 'N'
  AND   A.WLY_CD = #{wlyCd}
  AND   A.POST_ID = #{postId}

/** selectWlyPostCdList */
SELECT POST_ID , COMM_ID , POST_CD , (SELECT POST_NM FROM TB_COM_POST_CD_LANG WHERE DEL_YN = 'N' AND POST_ID = A.POST_ID AND LANG_CD = 'FR') AS POST_NM_FR , (SELECT POST_NM FROM TB_COM_POST_CD_LANG WHERE DEL_YN = 'N' AND POST_ID = A.POST_ID AND LANG_CD = 'EN') AS POST_NM_EN , (SELECT POST_NM FROM TB_COM_POST_CD_LANG WHERE DEL_YN = 'N' AND POST_ID = A.POST_ID AND LANG_CD = 'KO') AS POST_NM_KO , (SELECT POST_NM FROM TB_COM_POST_CD_LANG WHERE DEL_YN = 'N' AND POST_ID = A.POST_ID AND LANG_CD = 'AR') AS POST_NM_AR
, (SELECT POST_ADDR FROM TB_COM_POST_CD_LANG WHERE DEL_YN = 'N' AND POST_ID = A.POST_ID AND LANG_CD = 'FR') AS POST_ADDR_NM_FR , (SELECT POST_ADDR FROM TB_COM_POST_CD_LANG WHERE DEL_YN = 'N' AND POST_ID = A.POST_ID AND LANG_CD = 'EN') AS POST_ADDR_NM_EN , (SELECT POST_ADDR FROM TB_COM_POST_CD_LANG WHERE DEL_YN = 'N' AND POST_ID = A.POST_ID AND LANG_CD = 'KO') AS POST_ADDR_NM_KO , (SELECT POST_ADDR FROM TB_COM_POST_CD_LANG WHERE DEL_YN = 'N' AND POST_ID = A.POST_ID AND LANG_CD = 'AR') AS POST_ADDR_NM_AR , (SELECT COMM_NM FROM tb_com_comm_cd_lang WHERE DEL_YN = 'N' AND COMM_ID = A.COMM_ID AND LANG_CD = #{langCd}) AS COMM_NM FROM TB_COM_POST_CD A WHERE A.DEL_YN = 'N' AND A.COMM_ID = #{commId} ORDER BY POST_ID

mapper.insert:
/** insertWlyPostCd */
