mapper.select:
/* selectBtchMonitList */
SELECT A.SMLT_NO /** Numéro de simulation != 시뮬레이션번호 =! */ , TO_CHAR(TO_DATE(A.ANAY_STRT_DT, 'YYYYMMDD'), 'DD/MM/YYYY') || '~' || TO_CHAR(TO_DATE(A.ANAY_END_DT, 'YYYYMMDD'), 'DD/MM/YYYY') AS ANAY_DT /** Période d'analyse != 분석기간 =! */ , TO_CHAR(A.WKNG_STRT_DTTM, 'DD/MM/YYYY HH24:MI:SS') || '~' || TO_CHAR(A.WKNG_END_DTTM, 'DD/MM/YYYY HH24:MI:SS') AS WKNG_DT /** Période d'exécution du lot !== 실행기간 ==! */ , A.SMLT_STTS_CD /** Code du statut de simulation != 시뮬레이션상태코드 =! */ , TO_CHAR(A.FRST_RGSR_DTTM, 'DD/MM/YYYY') AS FRST_RGSR_DTTM /** Date et heure d'enregistrement != 최초등록일자 =! */ , A.FRST_REGST_ID /** Enregistré(e) par != 최초등록자ID =! */ , ( SELECT EMP_NM FROM TB_POTI_EMP WHERE PBSR_NO = A.FRST_REGST_ID ) AS FRST_REGST_NM /** Nom du premier enregistrant !== 최초등록자이름 ==! */ FROM TB_RSMI_SMLT_COMN A WHERE 1=1
#{smltSttsCdItem}
AND A.DCSH_TP_CD = #{dcshTpCd} /** Code De Type De Déclaration !== 신고서구분코드 ==! */
ORDER BY A.FRST_RGSR_DTTM DESC
ORDER BY A.SMLT_NO DESC

mapper.delete:
/** deleteSmltAplyCstm */
DELETE FROM TB_RSMI_SMLT_APLY_CSTM
WHERE SMLT_NO = #{smltNo}                       /** N° de simulation != 시뮬레이션번호 =! */

/** deleteSmltBaseNo */
DELETE FROM TB_RSMI_SMLT_BASE_NO
WHERE SMLT_NO = #{smltNo}               /** N° de simulation != 시뮬레이션번호 =! */

/** deleteSmltComn  */
DELETE FROM TB_RSMI_SMLT_COMN
WHERE SMLT_NO = #{smltNo}               /** N° de simulation != 시뮬레이션번호 =! */

mapper.update:
/** updateSmltBatcPrcc */
UPDATE TB_RSMI_SMLT_COMN
SET SMLT_STTS_CD = #{smltSttsCd}
  , LAST_CHPR_ID = #{lastChprId} /** Enregistré(e) par != 최종변경자ID =! */
  , LAST_CHG_DTTM = SYSTIMESTAMP /** Date et heure d'enregistrement != 최종변경일시 =! */
WHERE SMLT_NO = #{smltNo}               /** N° de simulation != 시뮬레이션번호 =! */
